import{_ as d,e as r,j as e,o as a}from"./app-BjYhvO_l.js";const o={};function s(n,t){return a(),r("div",null,t[0]||(t[0]=[e('<h1 id="старые-русские-слова" tabindex="-1"><a class="header-anchor" href="#старые-русские-слова"><span>Старые русские слова</span></a></h1><blockquote><p>Источник: <a href="https://yandex.ru/company/researches/2021/old-words" target="_blank" rel="noopener noreferrer">yandex.ru/company/researches/2021/old-words</a></p></blockquote><table><thead><tr><th>Слово</th><th>Пример употребления</th><th>Год</th></tr></thead><tbody><tr><td>авантажный</td><td>Костюм не особенно авантажен у меня</td><td>1916</td></tr><tr><td>аккомпаниатор</td><td>На другом вечере он выступал как аккомпаниатор романса: «Спи, моя девочка», который исполняла Аня Боровкова</td><td>1922</td></tr><tr><td>ангажемент</td><td>Оказывается, она, наконец, получила ангажемент, правда, очень скромный, в Музыкальную Драму, и гордилась этим невероятно</td><td>1917</td></tr><tr><td>англизировать</td><td>Две барышни и молодой человек, Белкин, — очень подтянутый, почти что англизированный, с лицом русского амура-полотера</td><td>1918</td></tr><tr><td>антипатичный</td><td>Заходил комендант Кремля Петерсон очень антипатичный тип</td><td>1922</td></tr><tr><td>антрепренер</td><td>Третьего дня я пила вечером чай у своего антрепренера, совсем как настоящая артистка, и там познакомилась с одним полковником, очевидно, большим знатоком театрального дела</td><td>1917</td></tr><tr><td>апаш</td><td>Старуха баронесса содержит старшего брата героя Алеши — совершеннейшего апаша</td><td>1913</td></tr><tr><td>ароматичный</td><td>Здесь два сорта мандарин, итальянские (с крупными семечками, сильно ароматичные и очень сочные, они светло-желты на вид, и пуповинка у них вытянута), и японские — без косточек, темнее цветом, нежнее, но и безвкуснее и без аромата</td><td>1930</td></tr><tr><td>аскетически</td><td>Для одного себя возьму минимум вещей и собираюсь жить аскетически, лишь бы не подрывать здоровья</td><td>1918</td></tr><tr><td>ассигновать</td><td>Еще кое-как дышит любительский театр, да и то потому, что на него ассигновано, по словам импресарио, губернским пролеткультом больше миллиона</td><td>1920</td></tr><tr><td>аудиенция</td><td>Он был необыкновенно любезен, дал нам аудиенцию в полчаса, очень хвалил Америку за ее богатства, щедрость и помощь</td><td>1928</td></tr><tr><td>аффектация</td><td>Михаил Сергеевич читает великолепно, как артист, без аффектации</td><td>1905</td></tr><tr><td>аэроплан</td><td>Опасаются налета аэропланов на город, хотя по мнению военных авторитетов это невозможно</td><td>1914</td></tr><tr><td>балаганить</td><td>Массу народа принесло; балаганили, танцевали, играли в игры</td><td>1906</td></tr><tr><td>балагурить</td><td>Его товарищ Смир как всегда старался балагурить и не стесняясь говорить избитые старые остроты</td><td>1907</td></tr><tr><td>баллотировать</td><td>Мы баллотировали несколько лиц в разные комитеты и делегации, и все это по очереди, с подачей прежде записок, на что также ушло немало времени</td><td>1917</td></tr><tr><td>беженство</td><td>Не в похвалу себе скажу все-таки, что я стала за годы беженства хорошей хозяйкой</td><td>1933</td></tr><tr><td>безалаберщина</td><td>Такой каши, такого кавардака, такой безалаберщины, какие получились у нас с приемом учеников на «рабфак», я никогда не видывал</td><td>1921</td></tr><tr><td>безвкусие</td><td>Но какое безвкусие, какое нахальство нужно для того, чтобы предложить переделать рисунок художника без его ведома и согласия</td><td>1937</td></tr><tr><td>бездельно</td><td>Бездельно и бесцельно провожу дни</td><td>1917</td></tr><tr><td>бездеятельный</td><td>Это оживило день, а то тоскливо было бездеятельно лежать с поломанной ключицей</td><td>1936</td></tr><tr><td>безжизненность</td><td>В нем сочеталась красота древней архитектуры, храмов, монастырей и кремля и величественного Волхова вместе с грязью улиц, полуразвалившимися домами и какой-то общей безжизненности</td><td>1919</td></tr><tr><td>безлесный</td><td>Дело в том, что в надежде на хорошую погоду мы остановились на плоской безлесной вершинке, и в случае дождя нам пришлось бы срочно сматываться куда-то в тартарары вниз в поисках места, где можно было бы натянуть палатку</td><td>1935</td></tr><tr><td>безличность</td><td>Вот именно обида невозможна при безличности среды, все равно что обижаться на землетрясение</td><td>1931</td></tr><tr><td>безлюдье</td><td>Вокруг столпились бледно-зеленые от оленьего мха сопки — тишина, покой, безлюдье и даже почти бескомарье</td><td>1936</td></tr><tr><td>безначалие</td><td>Грабежи, убийства, гибнущие лошади, расхищаемое на сотни тысяч имущество, безначалие, торговля с немцами за водку</td><td>1918</td></tr><tr><td>безотлучный</td><td>Все восхищаются молодцеватостью лейтенанта Сухомлина, который находится там безотлучно</td><td>1904</td></tr><tr><td>безотчетный</td><td>Дождь барабанит по крыше, по стеклам, воет ветер за окнами, и под эти унылые звуки в душу крадется тоска, иногда безотчетная, страшная тоска</td><td>1915</td></tr><tr><td>безрелигиозный</td><td>Чем больше я вижу, чем ближе всматриваюсь в эту новую безрелигиозную, «безбожную» жизнь, тем больше меня поражает ее безнадежная низменность и полная аморальность</td><td>1920</td></tr><tr><td>безыскусственный</td><td>Тогда же я весь превратился в зрение и слух, любуясь природой и вслушиваясь в безыскусственные повествования народных легенд нашим кучером</td><td>1905</td></tr><tr><td>беллетристика</td><td>Теперь из беллетристики я читаю Герцена, которого дала мне на лето Наташа</td><td>1910</td></tr><tr><td>беспечальный</td><td>Когда скорби и трудности обступают тебя со всех сторон и не видит человеческий глаз твой исхода и выхода, возложи на Господа печаль твою, молись и будь беспечен и беспечален</td><td>1924</td></tr><tr><td>бесплодно</td><td>В полном отчаянии отложили отъезд, бесплодно гадая, куда бы это мог деваться паспорт</td><td>1935</td></tr><tr><td>беспорядочность</td><td>Беспорядочность половой жизни какая-то архаическая</td><td>1925</td></tr><tr><td>беспрерывный</td><td>В город беспрерывной лентой едут обозы, везут хлеб с глубинных пунктов</td><td>1929</td></tr><tr><td>бессмыслие</td><td>И зачем люди волнуются, ссорятся, любятся, враждуют, строят козни, хватают друг друга за горло, опьяняются, работают, когда все это бессмысленно перед лицом смерти, перед безумием бессмыслия бытия</td><td>1924</td></tr><tr><td>бессодержательность</td><td>И далеко не сразу выясняется, что за всеми этими недосказанностями и глубокомыслием скрывается пустота и бессодержательность</td><td>1930</td></tr><tr><td>бессолнечный</td><td>Вчера ночью был только маленький мороз, нынче бессолнечный и теплый день, все тает и мокнет; и сейчас, к ночи — четыре градуса тепла</td><td>1919</td></tr><tr><td>бесстрастие</td><td>Дай божественного ко всему бесстрастия, благосклонного ко всему и блаженного безразличия</td><td>1918</td></tr><tr><td>бесформенность</td><td>А у Самойлова, например, вовсе не было, несмотря на бритость, впечатления англичанина, и рот был кустом и с русской бесформенностью</td><td>1905</td></tr><tr><td>бесцветность</td><td>Так мы ходили вокруг ЦК в поисках пропуска… В зеркальце виднелись бледно до бесцветности голубые глаза шофера, устремленные в странной неподвижности в одну точку: как будто это были не живые глаза, а специальные объективы для целесообразного фотографирования пути и последующей сигнализации двум пятерням, вращающим баранку руля</td><td>1934</td></tr><tr><td>бесцельность</td><td>Дни эти прошли в состоянии какой-то грустно лирической бесцельности</td><td>1931</td></tr><tr><td>бесчувствие</td><td>И он будет пить, пить до бесчувствия, пока хоть искра сознания останется в нем</td><td>1918</td></tr><tr><td>библиофил</td><td>И этот библиофил и архивист, типичный Herausgeber, не давший ни одной большой самостоятельной работы, попал в Академию, а теперь его же однопартийцы «доказывают» незначительность его размеров</td><td>1931</td></tr><tr><td>бисировать</td><td>Успех был менее шумный, чем прошлый раз, но часть зала, человек сто-двести, аплодировала с удивительной горячностью и настойчивостью, заставив меня бисировать четыре раза, причём даже гасили электричество и снова зажигали</td><td>1915</td></tr><tr><td>благовоспитанность</td><td>Под словом «культура» мы нередко подразумеваем образование, то есть школьную программу познаний, а то и степень благовоспитанности</td><td>1917</td></tr><tr><td>благодетельный</td><td>Всегда больных, испытавших благодетельное влияние врача, будет тянуть к нему, и никакими писаными рамками этого не вырвать</td><td>1919</td></tr><tr><td>благодушно</td><td>Так мы ехали благодушно и весело</td><td>1921</td></tr><tr><td>благожелательство</td><td>Оно несет собою и в себе некий дух благожелательства, общественной солидарности</td><td>1935</td></tr><tr><td>благонамеренный</td><td>Как ни безобразно это явление народного самоуправства, но многие, даже благонамеренные, люди одобряют его, настоль сильно накипела злоба против немцев</td><td>1915</td></tr><tr><td>благоугодно</td><td>Государю Императору благоугодно было всякие поездки и предложения отменить и принять меры, чтобы наши балетные артисты не разбегались бы</td><td>1910</td></tr><tr><td>благоуханный</td><td>В благоуханном плодовом саду, в просторном и роскошном, обильном воздухом и светом особняке наша маленькая коммуна (17 человек) чувствует себя превосходно</td><td>1920</td></tr><tr><td>блаженствовать</td><td>И мы в восторге и блаженствуем</td><td>1919</td></tr><tr><td>бодриться</td><td>Вчера вечером был у Лебедева, он бодрится, но нервы вздернуты у него до крайности</td><td>1926</td></tr><tr><td>браниться</td><td>Я никогда не слышал, чтобы он бранился или кого-нибудь ругал</td><td>1909</td></tr><tr><td>браслетка</td><td>Я ушел на полчаса в ресторан и оставил часы и золотую браслетку на ночном столике</td><td>1937</td></tr><tr><td>будировать</td><td>Те гадости, которые творятся кругом, и полная безнадежность истребить их обычным путем побудили меня подать официальный доклад об установлении административной высылки в находящиеся под красной властью пределы России наиболее злостных политиканов и сплетников, зловреднейших спекулянтов, подкапывающихся под нас эсеров и прочей будирующей нечисти, которых очень трудно достать по закону, в извилинах которого они искусно скрываются</td><td>1919</td></tr><tr><td>будущность</td><td>Будущность свою я представляю так: буду заниматься наукой, займусь политической или общественной деятельностью, буду развивать свою умственную жизнь, а потом уже любовь и прочее»</td><td>1918</td></tr><tr><td>бытописатель</td><td>Если бы сейчас сюда перо Тургенева, Толстого или других бытописателей</td><td>1918</td></tr><tr><td>вакация</td><td>Подчас охватывает желание плюнуть на все это, бросить и уехать в Пермь на вакации</td><td>1919</td></tr><tr><td>ватер</td><td>Утром чистил ватер и куриный хлев</td><td>1928</td></tr><tr><td>вдвигаться</td><td>Каждый вечер я жду мерных звуков вёсел, плеска воды, чёрная лодка, покачиваясь и шурша, вдвигается носом в песок, слышен стук сложенных на дно вёсел</td><td>1915</td></tr><tr><td>вделывать</td><td>На месте его ранения, в стене, вделан образ с лампадою, сделана надпись</td><td>1916</td></tr><tr><td>вековечный</td><td>Страна вековечных дикарей, идолопоклонников, и все идолы одинаково бездушны и грубы</td><td>1918</td></tr><tr><td>вероятие</td><td>Была ли тетя Маруся в числе пассажиров — неизвестно, но багаж она отправляла, по всему вероятию</td><td>1918</td></tr><tr><td>веселость</td><td>Мгновенно отлетела веселость, и охватила тоска</td><td>1905</td></tr><tr><td>взапуски</td><td>Вечером ко мне заходил Волконский сосед, и мы взапуски вспоминали общее наше студенческое прошлое</td><td>1918</td></tr><tr><td>взволнованность</td><td>Качка мне всегда приятна, тут было особенно — как-то это сливалось с моей внутренней взволнованностью</td><td>1905</td></tr><tr><td>вздор</td><td>Вот вам, Ниночка, в пику, коль вы на меня всякий вздор пишете Борюсе</td><td>1915</td></tr><tr><td>вздорожать</td><td>В Москве, как и Петрограде, сильно вздорожала жизнь</td><td>1915</td></tr><tr><td>видаться</td><td>Мы так давно не видались, сейчас ухожу к ней в номер и не вернусь до завтра, полудня, — ночую у неё, вернее — всю ночь проговорим</td><td>1901</td></tr><tr><td>визитировать</td><td>Я не принадлежу к числу людей, которые любят визитировать по большим праздникам</td><td>1934</td></tr><tr><td>виньетка</td><td>Передо мною лежит переплет от книжки, и на переплете виньетка: человек без сюртука и без оных, с выпяченными ребрами и с бычачьими мускулами, с закинутой назад головой и с горящей лучинкой в руках</td><td>1910</td></tr><tr><td>вкореняться</td><td>Кроме того, у всех (особенно же у безмерно самовлюбленного Альтмана) вкоренилось убеждение, что я старый сапог и «ничего в новом искусстве не понимаю»</td><td>1918</td></tr><tr><td>влагать</td><td>Целый день пишу письмо женушке, влагаю фотографии и другие данные, которые могут ее заинтересовать</td><td>1916</td></tr><tr><td>влачиться</td><td>Ей Богу, это часто в голову приходило, даже в походе, когда не идешь, а почти влачишься — кажется, что не дело делаешь, а развлекаешься</td><td>1914</td></tr><tr><td>влопаться</td><td>Сию минуту только чуть было не влопался, сев в присутствии штаб-офицера, но «так как сидел задом, получил прощение»</td><td>1914</td></tr><tr><td>влопываться</td><td>Сию минуту только чуть было не влопался, сев в присутствии штаб-офицера, но «так как сидел задом, получил прощение»</td><td>1914</td></tr><tr><td>влюбление</td><td>К первому, по-моему, чувство было искреннее и правдивее, оно началось у меня сразу, это было начало истинного влюбления</td><td>1920</td></tr><tr><td>внутренно</td><td>Мне хотелось бы сидеть с простыми верующими; там я внутренно сотворю свою молитву за Королеву, которую душевно уважал</td><td>1901</td></tr><tr><td>водворяться</td><td>После завтрака водворилась тишина и мне стало очень хорошо работать</td><td>1902</td></tr><tr><td>возвещаться</td><td>Вчера вечером нам прислали из Москвы экстренные выпуски газет, в которых жирным шрифтом возвещается ряд ошеломляющих новостей</td><td>1917</td></tr><tr><td>возглавление</td><td>И все-таки, по крайней мере сегодня, это единственный человек, вернее, единственное имя, пригодное для возглавления нового правительства</td><td>1936</td></tr><tr><td>воркотня</td><td>Я, удрученный ночной бессонницей и воркотней, смолкаю на всю дорогу от Ельца до Задонска</td><td>1908</td></tr><tr><td>воротиться</td><td>Воротясь, застал Ангелину, обедали, потом я уломал ее пойти на религиозное собрание</td><td>1912</td></tr><tr><td>воспоследовать</td><td>Обошлось, если не воспоследуют перемены, довольно милостиво</td><td>1921</td></tr><tr><td>воспрещаться</td><td>Кроме нескольких самых близких людей доступ к покойнику воспрещается</td><td>1935</td></tr><tr><td>восстановлять</td><td>Расставшись, долго на постели восстановлял в памяти сцены ночи</td><td>1918</td></tr><tr><td>вотировать</td><td>Все же тягостный факт для нас, белых, тот, что итальянский парламент вотировал желание признать большевиков</td><td>1922</td></tr><tr><td>впивать</td><td>Так горько и вместе с тем отрадно было впивать в себя и эту музыку, и эти дивные пушкинские слова</td><td>1931</td></tr><tr><td>вразброд</td><td>Бойцы стихийно вразброд ходили по улице, а многие ушли без разрешения в кино в соседнее подразделение</td><td>1938</td></tr><tr><td>всегдашний</td><td>Неужели все, что я делал и делаю, — только бегство от всегдашней неудовлетворенности</td><td>1939</td></tr><tr><td>всесветный</td><td>Если же это не поможет, то самому адмиралу надо принять командование над отрядом и идти на Читу, пусть японцы устраивают всесветный скандал и разоружают самого верховного главнокомандующего</td><td>1919</td></tr><tr><td>вспаивать</td><td>Мы видели парочку антилоп Пржевальского, взятых китайцами при рождении и воспитанных дома, вспоенных молоком коров</td><td>1908</td></tr><tr><td>вспомоществование</td><td>На вспомоществование разорённой войною Польше в Петрограде идёт сбор одежды и белья</td><td>1914</td></tr><tr><td>вспомянуть</td><td>В прошлом году, как ехали, вспомянуть страшно</td><td>1918</td></tr><tr><td>встрепанный</td><td>Это толстый рыжий встрепанный человек в совершенно пьяном виде</td><td>1916</td></tr><tr><td>втихомолку</td><td>Как после сами же мне рассказали, они повели здесь особенно веселую жизнь и каждый вечер, когда я усиленно писал дневник, втихомолку попивали спиртные напитки</td><td>1926</td></tr><tr><td>вчерне</td><td>Постройки производят теперь благоприятное впечатление, двор чистый, шоссе вчерне закончено</td><td>1901</td></tr><tr><td>вчуже</td><td>Не могу осуждать улыбавшихся, я бы сама улыбалась и насмешничала, будь это вчуже</td><td>1933</td></tr><tr><td>выбранить</td><td>Выбранил я Петра Исикава плохим дипломатом и решился действовать иначе</td><td>1905</td></tr><tr><td>выбриваться</td><td>После кофе с тостами выбрился, опять без усов — так лучше — даже Миф стал одобрять</td><td>1925</td></tr><tr><td>вызвездить</td><td>Ночью неожиданно, как на сцене, вызвездило, и приехали в морозное ясное утро в Петербург</td><td>1906</td></tr><tr><td>выказываться</td><td>Удивительное стойкость выказывается во всём обществе</td><td>1915</td></tr><tr><td>выкликать</td><td>Старший барака приказал нам построиться, а сам взял список и стал выкликать по фамилии</td><td>1915</td></tr><tr><td>выкупаться</td><td>Успел до обедни выкупаться в море</td><td>1900</td></tr><tr><td>выпрашиваться</td><td>Идти не могу совсем, сапоги в глинистой грязи снимаются, задыхаюсь, выпросился на повозку и верст 15 ехал на спине в интендантской повозке</td><td>1914</td></tr><tr><td>вырезывать</td><td>Читаю, вырезываю из газет, записываю в альбомы, уничтожаю мух и чувствую себя разбитым, усталым и неудовлетворенным жизнью</td><td>1934</td></tr><tr><td>вырешить</td><td>Суд вырешил взыскать с пользу Тупикина</td><td>1926</td></tr><tr><td>выругать</td><td>В этом отношении Художественному театру надо не столько бояться газет, которые их ругают, как например «Новое время» — выругало чисто площадным образом и конец</td><td>1915</td></tr><tr><td>выслушание</td><td>Сегодня меня вызывали в Исполком для выслушания акта о привлечении меня и елабужских священников за крестные ходы по волости со Спасителем к ответственности</td><td>1924</td></tr><tr><td>высокоталантливый</td><td>Это показывает, сколько личного, своего заветного вкладывает даже такой высокоталантливый писатель, как Тургенев, во все, что он пишет</td><td>1907</td></tr><tr><td>высокоторжественный</td><td>Первый вечер нашей Пасхи был высокоторжественный, как никогда</td><td>1922</td></tr><tr><td>вытанцовываться</td><td>А впрямь, что-то не вытанцовывается наше дело в Персии</td><td>1920</td></tr><tr><td>вытребовать</td><td>Вечером начальник Минной дивизии вытребовал подводные лодки к себе, к Михайловскому маяку</td><td>1915</td></tr><tr><td>выученик</td><td>Он выученик старой школы, и потому никаких догматов и предвзятостей у него нет</td><td>1939</td></tr><tr><td>выхлопатывать</td><td>Все хожу выхлопатываю вид на жительство взамен украденной у меня трудовой книжки</td><td>1924</td></tr><tr><td>вычерчивать</td><td>Они позволяют вычертить профиль дна Северного Ледовитого океана от Северного полюса до Гренландского моря</td><td>1938</td></tr><tr><td>вышепоименованный</td><td>Кроме вышепоименованных лиц, был хормейстер, старик Авранек, и несколько человек, представителей технической части, освещения, бутафории и пр</td><td>1927</td></tr><tr><td>гармонически</td><td>Говорят, у Аллы было все гармонически слито</td><td>1938</td></tr><tr><td>гикать</td><td>Тьма, морозный туман, красная озорь гикает</td><td>1917</td></tr><tr><td>главноначальствующий</td><td>Правление дороги отказало в кредитах на содержание штата Главноначальствующего, указав, что частной дороге нет никакого дела до таких учреждений</td><td>1919</td></tr><tr><td>гнести</td><td>Приходилось слышать от вполне интеллигентных женщин, находящихся замужем, что семейная жизнь засасывает, притупляет, гнетет, что иногда хочется все бросить: и мужа, и детей, и хозяйство — и бежать куда глаза глядят</td><td>1920</td></tr><tr><td>головотяп</td><td>Мой фельетон о советских головотяпах, желающих «подержаться за лозунги», Чагин угробил</td><td>1926</td></tr><tr><td>голодноватый</td><td>С питанием, хотя и не голодновато, но тощевато и скудновато</td><td>1934</td></tr><tr><td>горший</td><td>Заваривается новая каша, может быть, еще горшая, чем нас угощали в Москве</td><td>1917</td></tr><tr><td>громоздиться</td><td>Туда к северу, где громоздятся горы, виднеются уже наш берег Аргуни и первый русский поселок</td><td>1904</td></tr><tr><td>гулко</td><td>Шаги гулко отдаются в ночной тишине</td><td>1919</td></tr><tr><td>даровитый</td><td>Володя — после писатель, либерал — был самый даровитый из них</td><td>1909</td></tr><tr><td>даровой</td><td>Ездил на трамвае в Музей и обратно; даровой билет с 1 ноября отменен, пришлось заплатить по 2000 р</td><td>1921</td></tr><tr><td>дезабилье</td><td>Около стула, на котором он сидит, стоит женщина дезабилье, одну ногу поставив на стул</td><td>1929</td></tr><tr><td>действование</td><td>Прямо какая-то потребность в действовании заложена в моей душе</td><td>1917</td></tr><tr><td>декламировать</td><td>Хотя мы с сестрой и не знали ни латыни, ни греческого, ты часто декламировал нам с возвышенным пафосом Гомера, Горация, Цицерона</td><td>1930</td></tr><tr><td>декретировать</td><td>Везде на станциях уже 14 февраля, так как большевики декретировали переход на новый стиль</td><td>1918</td></tr><tr><td>деланно</td><td>Женщина повернулась и презрительно, но деланно фыркнула</td><td>1918</td></tr><tr><td>деловитость</td><td>Он пользуется огромным уважением и популярностью за свою деловитость, административный талант и еще больше — за неподкупную порядочность и честность</td><td>1923</td></tr><tr><td>денной</td><td>Выражаясь классически, последнюю поездку он провел в денном и ночном разврате</td><td>1938</td></tr><tr><td>депутация</td><td>Депутация учащихся беседовала с директором и просила его принять надлежащие меры</td><td>1932</td></tr><tr><td>деятельно</td><td>Плеве деятельно взялся за чистку Петербурга, только, ой, не напороться бы и ему на собственную мину</td><td>1904</td></tr><tr><td>дивно</td><td>О, дивно служение священническое, о, бесценный дар, вверенный мне окаянному</td><td>1924</td></tr><tr><td>дивоваться</td><td>Педагоги, не посвященные еще в секрет созыва заседания, понятно, недоумевают и дивуются, откуда у учеников такое внезапное ярко выраженное прилежание; скоро, однако, все выясняется, и последние дни четверти сами учителя посвящают поправкам и репетициям</td><td>1906</td></tr><tr><td>директорство</td><td>Его прошение удовлетворено, но он отставлен не только от директорства, но и от профессуры, с причислением к министерству</td><td>1911</td></tr><tr><td>дискредитирование</td><td>Ну что же, может быть, тогда власть будет крепче, внушительнее и не станет подвергаться дискредитированию со стороны товарищей Ленина, Стеклова и им подобных</td><td>1917</td></tr><tr><td>дичиться</td><td>Первые предварительные беседы с ученицами; перестают меня дичиться, а вечером совсем разговорились — и посыпались вопросы, чувствуешь, как пытливо работает детская душа, как все в ней неоформлено, но требовательно</td><td>1912</td></tr><tr><td>длиннота</td><td>Такую длинную, нескладную фразу написал лишь только для длинноты, но очень длинной она не получилась</td><td>1938</td></tr><tr><td>до невероятия</td><td>Весело было до невероятия! И на аэроплане катались, и разные шутки выкидывали, и танцевали, а угощение было — ну!</td><td>1923</td></tr><tr><td>доброжелательство</td><td>Трудно побѣдить дурное расположеніе духа и недоброжелательство къ человѣку, но можно</td><td>1906</td></tr><tr><td>довольствовать</td><td>Я сопровождаю своих друзей в Европейскую гостиницу, где их будут довольствовать за счет дворцового ведомства</td><td>1916</td></tr><tr><td>дождить</td><td>Было холодно и слегка дождило</td><td>1938</td></tr><tr><td>дожидать</td><td>Сразу разсыпал пять человек в цепь и стал дожидать взвод</td><td>1921</td></tr><tr><td>доискиваться</td><td>Своего фельдшера Устюгова я теперь не доищусь: он все ораторствует то в «республиканском клубе», то в других собраниях</td><td>1917</td></tr><tr><td>доканчивать</td><td>Докончил сочинять вступление к симфонии</td><td>1929</td></tr><tr><td>докучный</td><td>Маленький садовник все эти докучные хлопоты возлагает на собственников сетей, сам он пользуется только рыбой и омарами</td><td>1933</td></tr><tr><td>долее</td><td>Мы остались всю зиму только для мама в Чернигове, по ее просьбе охраняя детей при ее поездке в Петербург, потом остались по ее просьбе и долее, боясь отъездом своим причинить ей неприятность</td><td>1908</td></tr><tr><td>долженствовать</td><td>Уже идут разговоры об этих фильмах, долженствующих определить лицо текущего сезона</td><td>1929</td></tr><tr><td>долинка</td><td>Но рек в этих лесах очень много, и в их долинках всегда зеленеет трава, сюда идет скотина, и тоже сюда, как только встал из берлоги, идет на светлянку на солнышко погреться и поохотиться у берега медведь</td><td>1935</td></tr><tr><td>домовладелица</td><td>За две недели своего пребывания в Киеве большевики успели сделать у ее дяди несколько обысков, арестовать его с женой; у ее свекрови реквизировали муку, по доносу жильцов, так как она – это несчастное существо – домовладелица</td><td>1919</td></tr><tr><td>домовничать</td><td>Вечером наши пошли к Варваре Павловне, а я остался домовничать</td><td>1905</td></tr><tr><td>доплетаться</td><td>Домой едва доплелся — так устал</td><td>1920</td></tr><tr><td>допьяна</td><td>Купил я ему полбутылки вина на свои деньги, и напились с ним допьяна</td><td>1925</td></tr><tr><td>досадовать</td><td>Я в душе очень досадовал, что не мог сдержаться и позволил себе подобную невежливость</td><td>1900</td></tr><tr><td>достукаться</td><td>Словом, достукались, а что из этого выйдет, невозможно предвидеть</td><td>1917</td></tr><tr><td>дотащиться</td><td>Пара с величайшим трудом дотащилась до станции</td><td>1921</td></tr><tr><td>дочесть</td><td>Потом дочел предисловие и биографию Сервантеса и лег спать</td><td>1920</td></tr><tr><td>дочиста</td><td>Я верю, но мне кажется, что я не должен верить, что вера — это еще остаток моего еще неразграбленного имущества, как у обывателя, которого обобрали дочиста, но он еще не может понять это и все хватается за какие-то за свои остатки</td><td>1918</td></tr><tr><td>дрянность</td><td>Тут налицо дрянность, самая настоящая душевная и моральная дрянность</td><td>1918</td></tr><tr><td>естественник</td><td>Во всяком случае, «выучиться науке», стать естественником — это огромный шаг для человека с развитым сознанием</td><td>1936</td></tr><tr><td>жалование</td><td>Вчера объявили о прибавке жалованья по 80 руб</td><td>1905</td></tr><tr><td>ждаться</td><td>Все ждалось, вот дойдешь до вершины, что-нибудь получится особенное</td><td>1932</td></tr><tr><td>жизнепонимание</td><td>И как дико то, что прожив 22 года я ни разу не думала, что все основы жизнепонимания лежат в химии</td><td>1912</td></tr><tr><td>жилица</td><td>Кроме нас и Платера была еще жилица, миловидная блондинка; немного пели Анюта, я и дочка Кати</td><td>1920</td></tr><tr><td>журфикс</td><td>Я не раз бывал на ее журфиксах (о чем есть несколько строк в этих тетрадях), а однажды навестил в неназначенный час, и она показала мне парочку своих обезьянок</td><td>1908</td></tr><tr><td>жуткость</td><td>Жуткость покинутого города и свист пуль</td><td>1922</td></tr><tr><td>забаллотировать</td><td>Во вчерашнем общем собрании Исторического музея в местком выбран Алмазов, забаллотирован Бабенчиков</td><td>1925</td></tr><tr><td>забаллотировывать</td><td>Во вчерашнем общем собрании Исторического музея в местком выбран Алмазов, забаллотирован Бабенчиков</td><td>1925</td></tr><tr><td>заведывание</td><td>Мне поручено заведыванье всеми стенограммами с литературной стороны и привлечение помощников для завершения работы</td><td>1917</td></tr><tr><td>заволакивать</td><td>Такой какой-нибудь поступок — на редкость чистый и благородный, светлая искра души человеческой, и я чувствую, как слезы неудержимо заволакивают мои глаза</td><td>1917</td></tr><tr><td>завтрема</td><td>Впрочем, к завтрему, может быть, высохнет, потому что второй день погода стоит превосходнейшая</td><td>1933</td></tr><tr><td>заглазно</td><td>Коровина и Головина он все зовет к себе, а заглазно их ругает</td><td>1907</td></tr><tr><td>заготовление</td><td>С Рерихом и подошедшим Колей Лансере мы занялись заготовлением некоторых бумаг и среди них — отказ Совещания от своего дальнейшего существования</td><td>1917</td></tr><tr><td>задохся</td><td>После каждого приступа кашля, чтобы он не задохся, приходится ему менять трубочку, которая у него в горле</td><td>1912</td></tr><tr><td>задушевно</td><td>Как она хорошо и задушевно поет</td><td>1915</td></tr><tr><td>зазябнуть</td><td>Я побоялась, что мальчишки очень зазябнут и отправила их домой</td><td>1938</td></tr><tr><td>заимообразный</td><td>Дал заимообразно Павлу Николаевичу Сапунову 10 000 р</td><td>1915</td></tr><tr><td>закаиваться</td><td>Тихий думал поухаживать за ней, но получил от матери ее такое atande, что закаялся другой раз и другим поручил то же</td><td>1920</td></tr><tr><td>заканчивание</td><td>Теперь все силы надо обратить на войну, на ее поднятие на плечи, на ее напряженное заканчивание</td><td>1917</td></tr><tr><td>законодательствовать</td><td>Вдали видется деспотизм дикой толпы, не признающей ничего святого, ни религии, ни патриотизма, ни семейства, ни поэзии… собрание героев «Дна» Горького, законодательствующих над Россией</td><td>1906</td></tr><tr><td>законоучитель</td><td>Арестованы бывший законоучитель I мужской гимназии протоиерей Александр Каменский и настоятель Кладбищенской церкви Феофилий Булдовский</td><td>1920</td></tr><tr><td>закоченеть</td><td>Ей надо было вернуться домой, я пошел ее провожать к трамваю, и мы чуть не закоченели, ожидая его чуть ли не полчаса</td><td>1940</td></tr><tr><td>зала</td><td>Потом вышел в залу, лег на софу и слушал Годара, Чайковского, Рубинштейна</td><td>1905</td></tr><tr><td>запаздывать</td><td>На лекцию Кунича я немного запоздала, но, в общем, время на курсах провела, как всегда, приятно</td><td>1918</td></tr><tr><td>запакостить</td><td>А затем она мастер на то, чтобы найденное хорошее запакостить, замусолить, засахарить</td><td>1913</td></tr><tr><td>запанибрата</td><td>В вину мне ставится также и то, что у меня с ученицами VIII класса «товарищеские отношения», что я с ними «запанибрата», заступаюсь за них: «остается только на него юбку надеть», — развязно комментирует Б-ский</td><td>1913</td></tr><tr><td>заправила</td><td>Она — заправила «ночных» предэкзаменационных занятий, я же — ведомый (а что еще остается делать?)</td><td>1940</td></tr><tr><td>запрещение</td><td>Запрестольных крестов и хоругвей не брали в силу запрещения совершать крестный ход из-за эпидемических заболеваний</td><td>1929</td></tr><tr><td>запродавать</td><td>Разочаровываюсь в «идейности» Тюнина — он просто торговец, маскирующийся любовью к старине; несколько раз пытался дорого запродать мне вещи, но я счастливо уклонялся</td><td>1919</td></tr><tr><td>запрятываться</td><td>Ласточки витают, медленно на большой высоте; голуби съежились в комочек, нахохлились, собаки запрятались по углам, съежившись в калачики</td><td>1908</td></tr><tr><td>запьянствовать</td><td>Третьего дня Блок рассказывал, как он с кем-то в Альконосте запьянствовал, засиделся, и их чуть не заарестовали: — Почему сидите в чужой квартире после 12 час</td><td>1919</td></tr><tr><td>зарез</td><td>Жорж ни за что не хотел давать, он очень бережет кирпич, необходимый для ремонта наших собственных строений, а когда тот стал очень слезно просить, говоря, что ему до зарезу нужно, то послал его к Лёле</td><td>1919</td></tr><tr><td>заручка</td><td>Бежать? Но куда? Как? Нужны связи, нужны заручки</td><td>1918</td></tr><tr><td>засветло</td><td>Прибыли в Варшаву вечером около 9-ти, еще засветло, когда только начинало смеркаться</td><td>1906</td></tr><tr><td>застопорить</td><td>Застопорили машины и стали на правый якорь при сингапурском большом рейде города Сингапура на глубине 9 саж</td><td>1913</td></tr><tr><td>затруднительность</td><td>Отправляя меня, он сказал, что знает затруднительность подобной миссии и просит меня съездить, чтобы как-нибудь успокоить расходившегося старика</td><td>1902</td></tr><tr><td>затушевывать</td><td>Я привыкла признаваться себе во всём и не скрываю от себя и не затушёвываю ничего</td><td>1939</td></tr><tr><td>заутренний</td><td>Это было в тот заутренний час перед восходом солнца, когда, если взялся мороз, то борется он, нарастая с такой силой, с такой быстротой, что с каждой минутой чувствуешь</td><td>1925</td></tr><tr><td>захватный</td><td>Эсеры обещают всю землю народу и призывают эту землю брать захватным порядком — я это только что видел, так как был в деревне</td><td>1917</td></tr><tr><td>захворать</td><td>Но вот уже четвертый день, как она опять слегла и захворала довольно серьезно</td><td>1907</td></tr><tr><td>захолодать</td><td>К вечеру захолодало, и сейчас всего два градуса</td><td>1918</td></tr><tr><td>захолонуть</td><td>У меня захолонуло сердце: а вдруг они уже были там в то время, когда муж мылся в бане, и могли встретить его на улице</td><td>1920</td></tr><tr><td>зачинивать</td><td>Занимался починкой велосипеда, у него не в порядке камера, все зачинил и исправил</td><td>1937</td></tr><tr><td>зашлепать</td><td>Первая весна — разлив, преображение планеты, вторая — когда лягушки запрыгают, зашлепают тяжело, заквакают, запоют, откроется лягушье царство, и тут вместе с ними совы</td><td>1911</td></tr><tr><td>защитительный</td><td>В своей защитительной речи Керенский заявил: «Обвиняемые никогда не стремились вызвать брожение в армии; никогда не желали поражения нашей армии; они никогда не протягивали руки неприятелю через головы тех, кто умирает на фронте, защищая родину»</td><td>1916</td></tr><tr><td>звониться</td><td>Я решил покончить сегодня же со своим неопределенным состоянием и стал звониться к Беталу</td><td>1936</td></tr><tr><td>здешний</td><td>Ходили с Татианой в здешние лавки, накупили разной дребедени и сластей</td><td>1907</td></tr><tr><td>здорование</td><td>Василий Иванович при здоровании очень долго держал мою руку в своей и потом спрашивал: «Ничего, что я вас зову Аличкой, вы не сердитесь</td><td>1907</td></tr><tr><td>земно</td><td>Земно кланяюсь славному полку, стяжавшему мне эту великую воинскую награду</td><td>1916</td></tr><tr><td>значительность</td><td>Грабарь занят собою, своею значительностью</td><td>1922</td></tr><tr><td>знойно</td><td>С утра прохладно, но уже в 11-м часу становится знойно и солнце ослепительно</td><td>1912</td></tr><tr><td>зыбь</td><td>В море дул свежий ветер, разводивший крупную зыбь</td><td>1914</td></tr><tr><td>извериваться</td><td>В душе изверившегося в себя и в свои силы безвольного интеллигента все-таки теплилась слабая надежда на самооправдание, и именно потому так раздражало и оскорбляло всякое превосходство</td><td>1922</td></tr><tr><td>известие</td><td>Приносится известие, что в порт входят иностранные пароходы</td><td>1919</td></tr><tr><td>извинительный</td><td>Старался дать резюме своей поездки в Ростов и говорил все время извинительным тоном, потому что денег не привез ни нам, ни Браиловским</td><td>1918</td></tr><tr><td>изглаживать</td><td>К счастью, прощанье, вполне дружеское, изгладило эти неприятности, и я сел в вагон, успокоенный и даже удовлетворенный московскими днями</td><td>1909</td></tr><tr><td>издали</td><td>Наконец подъем на старую виднеющуюся издали темным конусом морену</td><td>1934</td></tr><tr><td>издергиваться</td><td>Настроение у пехоты подавленное, впрочем, оно и понятно – все ведь издергались и устали нервно</td><td>1917</td></tr><tr><td>издерживать</td><td>Почти всю эту сумму издержали на свою поездку</td><td>1908</td></tr><tr><td>изживаться</td><td>Ошибка в законодательстве до тех пор не погасится, пока не изживется</td><td>1936</td></tr><tr><td>иззябнуть</td><td>Приходим домой иззябшие, усталые, голодные</td><td>1916</td></tr><tr><td>изламывать</td><td>Все квартиры разграблены, музей уничтожен, кабинет естественно-исторический и физический разорены, все перебито, изломано, изорвано</td><td>1919</td></tr><tr><td>изломаться</td><td>Изорвёшься, изнервничаешься, изломаешься тут весь — вот и городишь ерунду и взять себя в руки не можешь</td><td>1901</td></tr><tr><td>изменнический</td><td>Хорошенькая армия, в которой возможны безнаказанно такие заявления; платные немецкие разрушители могут только радоваться на быстрые и роскошные результаты своих трудов и просить прибавки за успешное выполнение своей изменнической работы</td><td>1917</td></tr><tr><td>изминать</td><td>Все, все новое, и все это тяжело легло на душу, перевернуло все принципы, разрушило, измяло</td><td>1922</td></tr><tr><td>измолачивать</td><td>К вечеру сегодня измолотили мой хлеб и свезли домой</td><td>1928</td></tr><tr><td>измучивать</td><td>Собаки не измучены, побежали — только бегу давай</td><td>1935</td></tr><tr><td>изнемогать</td><td>И совсем изнемогающие от усталости, мы достигли, наконец, нашей цели</td><td>1920</td></tr><tr><td>изнуренный</td><td>Вахмистр 1-й сотни уже приметил и доложил своему командиру, что представлялся прекрасный случай обменять изнуренных и подбитых лошадей свежими</td><td>1904</td></tr><tr><td>изолгаться</td><td>А расхищение достигло, кажется, своего максимума, люди изолгались, стали несчастны, или, скорее, весь низкий элемент поднял голову, заговорил о себе</td><td>1919</td></tr><tr><td>изорвать</td><td>Я выбежал к швейцару и сказал: — Если мне будет письмо из-за границы, то прошу мне не доставлять, а изорвать его</td><td>1909</td></tr><tr><td>изругать</td><td>А когда я играл Сонату, то откуда ни возьмись принесло Лядку, того самого Лядку, который год назад изругал эту Сонату в прах</td><td>1910</td></tr><tr><td>иллюминовать</td><td>Вечером ходил в Успенье ко всенощной: церковь была иллюминирована разноцветными фонариками</td><td>1922</td></tr><tr><td>инсинуировать</td><td>Знаменитый Божовский, рекомендация Мосолова и о котором он так беспокоится, продолжает инсинуировать в газетах и угрожать Московской конторе какими-то разоблачениями</td><td>1911</td></tr><tr><td>инспиратор</td><td>Про него говорят, что он инспиратор антирусских настроений Клемансо</td><td>1919</td></tr><tr><td>инспирация</td><td>Пахнет инспирацией Оссовского, самолюбие коего было обижено отклонением его «редакторства» при издании «Летописи»</td><td>1909</td></tr><tr><td>инфлюэнца</td><td>Я второй день не показываю носа на улицу, боясь простуды: каждую зиму я болею по несколько раз инфлюэнцей</td><td>1931</td></tr><tr><td>искание</td><td>Красота, чувственность, быстрая переменчивость, религиозность, вечное искание ее и истины — вот характеристика моего мужа</td><td>1910</td></tr><tr><td>искательный</td><td>— Виноват, — ответил он искательным, голосом, — заправлял шину, лопнула по дороге</td><td>1916</td></tr><tr><td>искренно</td><td>И я желаю им взаимного счастья — искренно и бескорыстно</td><td>1922</td></tr><tr><td>искусник</td><td>Не забыть, что дятел из дерева извлекает свою брачную песню: он только искусник, музыкант, поет у него дерево, как поет дерево у скрипача</td><td>1937</td></tr><tr><td>испитой</td><td>Он сегодня был на Главном почтамте и наблюдал массу серого степного народа; все они испитые, иссохшие, оборванные, грязные, изнуренные</td><td>1937</td></tr><tr><td>исподличаться</td><td>Господи, как исподличался, изгнусился человек — и такой талантливый</td><td>1907</td></tr><tr><td>испрашивать</td><td>По окончании докладов я подошел к Мышлаевскому: по его словам, он уже убедил Сухомлинова в необходимости пересмотреть вопрос о крепостях и испросил вчера у государя разрешение лично осмотреть крепости</td><td>1909</td></tr><tr><td>испрошение</td><td>Он неверно понял советы, которые давал ему Митя, предупреждая не пользоваться вещами, принадлежащими жене и мне, без предварительного испрошения нашего согласия</td><td>1911</td></tr><tr><td>исстрадаться</td><td>Истомлённые, исстрадавшиеся лица… Печальные картины, не бьющие уже по нервам, как в начале войны</td><td>1905</td></tr><tr><td>исступленный</td><td>Под шум исступленных криков мы трое говорим о начавшейся войне</td><td>1914</td></tr><tr><td>истасканный</td><td>А к избитому и заманчивому лозунгу «равенство, свобода и братство», а равно как к истасканной формуле «долой войну», «без аннексий и контрибуций», «свободное самоопределение народностей» необходимо внести большие поправки, чтобы все эти лозунги не сделались ложью и не являлись недомыслием</td><td>1917</td></tr><tr><td>истомляться</td><td>Истомилась душа по ощущению нужности, занятости, работы</td><td>1940</td></tr><tr><td>истрачиваться</td><td>Она отняла свежесть и непосредственность, нашу искренность, и главное, нашу чуткость, а сколько душевной теплоты истратилось по мелочам и не по мелочам среди людей</td><td>1920</td></tr><tr><td>исхлопотать</td><td>По-видимому, он и был сыном Бострома, но матери удалось исхлопотать разрешение Сената на присвоение сыну фамилии и титула Толстых (у меня где-то хранится этот документ)</td><td>1937</td></tr><tr><td>исходатайствовать</td><td>Генерал-квартирмейстер Генерального штаба Дубасов написал мне, прося, чтоб я исходатайствовал ему у Государя назначение в Военный Совет, о чем уже всеподданнейше просил Палицын</td><td>1908</td></tr><tr><td>исхудалый</td><td>На берегах речки пасся исхудалый скот — лошади, коровы, ослы</td><td>1908</td></tr><tr><td>казенный</td><td>Она просит о казенной даче в Петровском парке</td><td>1908</td></tr><tr><td>канителиться</td><td>После обеда долго канителились, а, потом, я пошёл на станцию заниматься, но скоро туда пришёл Коля и позвал в баню</td><td>1918</td></tr><tr><td>кассировать</td><td>Кассировать нечем, денег нет, все вышли</td><td>1935</td></tr><tr><td>кафешантан</td><td>В Харбине — два балагана-театра, два цирка, несколько таких же балаганов-кафешантанов, китайский театр</td><td>1904</td></tr><tr><td>квартирохозяин</td><td>Был в снятой комнате — ничего не приготовлено, это настроило меня минорно: положиться на квартирохозяина трудно</td><td>1937</td></tr><tr><td>кейфовать</td><td>Притащили груш, бананов, ликёрных конфет и кейфовали</td><td>1913</td></tr><tr><td>кинемо</td><td>Потом были в кинемо, Юрочку отправили домой спать, а сами ужинали в «Вене» у Дмитрия</td><td>1913</td></tr><tr><td>киноаппарат</td><td>«Человек с киноаппаратом» мог стать прекрасной картиной о современной советской Москве, о жизни ее улиц, заводов, учреждений, общественных мест</td><td>1929</td></tr><tr><td>кинофабрика</td><td>Меня вызывают на кинофабрику 3 мая</td><td>1927</td></tr><tr><td>кисея</td><td>Небо покрыто кисеей легких, прозрачных, белесоватых облаков, смягченных благотворным действием солнца, что дает возможность комарам беспрепятственно развернуть свою деятельность</td><td>1940</td></tr><tr><td>клавир</td><td>Играл с ним кусочек его 3-й Симфонии по делаемому клавиру</td><td>1914</td></tr><tr><td>классичный</td><td>Я стал говорить, что стихи «Клевета» холодны и слишком классичны</td><td>1922</td></tr><tr><td>книгопродавец</td><td>Ни один книгопродавец не мог продать и пяти экземпляров</td><td>1922</td></tr><tr><td>книжность</td><td>Мне тяжело вспоминать мою молодость: в ней слишком много книжности, холодного стоицизма, подавленных стремлений; борьба за существование и сознательный отказ от «полноты» жизни во имя ее «цельности» сужали и леденили впечатления</td><td>1921</td></tr><tr><td>кокотка</td><td>Он называл ее своей женой, но вел себя с ней как с кокоткой, чем она нисколько не смущалась</td><td>1921</td></tr><tr><td>колодезь</td><td>Тропинка обходит колодезь с журавлем, затем стелется дальше</td><td>1914</td></tr><tr><td>комизм</td><td>Он пляшет так серьезно, будто совершается что-то священное — это-то и составляет комизм</td><td>1915</td></tr><tr><td>компатриот</td><td>Мои три компатриота явились сегодня утром ко мне в кабинет</td><td>1917</td></tr><tr><td>консерваторка</td><td>Оказывается, хозяйка сдала салон — и ещё кому — консерваторке, с пианино</td><td>1901</td></tr><tr><td>конфекта</td><td>После занятий зашел к «Мюру», купил хлеба и коробочку конфект для Ляли</td><td>1927</td></tr><tr><td>конфузиться</td><td>Он не совсем еще каналья и очень конфузился и бегал во все стороны глазами</td><td>1919</td></tr><tr><td>концертировать</td><td>Он очень хочет иметь дело со мной, но говорит, что настоящий успех в здешних странах можно иметь только концертируя в паре</td><td>1918</td></tr><tr><td>кооптировать</td><td>Не прошло и двух недель, как я был кооптирован в Совет нашим Кварталом</td><td>1919</td></tr><tr><td>корректив</td><td>Затем после добавочных коррективов выяснилось, что питается этот зверь оленями и сохатыми, что караулит он их на деревьях и бросается на них, после чего «маленький медведь» с несомненностью превратился в большую рысь — «онаки» по-тунгусски и «сеган» по-якутски</td><td>1936</td></tr><tr><td>кофей</td><td>В 12 выпил две чашки кофею, чаю, молока</td><td>1908</td></tr><tr><td>кочковатый</td><td>Местность гнусная — высоко-кочковатая тундра, лошадям тяжело ехать</td><td>1933</td></tr><tr><td>крамольник</td><td>Рассуждай так студиозы или обыкновенные штатские люди — были бы сопричислены к лику крамольников и революционеров</td><td>1916</td></tr><tr><td>круговращение</td><td>Бог непременно в человеке, но начала нет, тут круговращение: весна, лето, зима, осень</td><td>1908</td></tr><tr><td>крылатость</td><td>И это чувство растаявшей глыбы на сердце, это ощущение крылатости, как птица в синеве неба, дает не счастье, не радость, но блаженство, какого-то окончательного ведения, всего во всем и всего в себе, всякого всячества, мира в единстве</td><td>1923</td></tr><tr><td>культурность</td><td>В восторге от английской культурности</td><td>1926</td></tr><tr><td>купальщица</td><td>Хочет приобрести мою декабрьскую картину (радуга, лодка, купальщицы) и согласен на мои условия</td><td>1922</td></tr><tr><td>курьезно</td><td>До бешенства довели комары, но курьезно: вечером они сразу все попрятались, несмотря на тихую и теплую погоду</td><td>1917</td></tr><tr><td>курьёзно</td><td>До бешенства довели комары, но курьезно: вечером они сразу все попрятались, несмотря на тихую и теплую погоду</td><td>1917</td></tr><tr><td>кустарь</td><td>Индивидуалист, кустарь-одиночка в науке сейчас такое же недоразумение, как недоразумение в прошлом до революции представляли бы коллективы и бригады в лабораториях и институтах, организованные для проработки того или иного вопроса</td><td>1929</td></tr><tr><td>кухмистерская</td><td>Я хорошо пообедал в какой-то польской кухмистерской</td><td>1915</td></tr><tr><td>лакействовать</td><td>И, в самом деле, кому нужен лингвист, историк литературы, просто культурный энциклопедист, не имеющий ни «узкой специальности», ни репутации лакействующего попугая</td><td>1932</td></tr><tr><td>лаконический</td><td>В августе или сентябре получил от скульпторши открытку с лаконическою фразой: «Деньги я получила»</td><td>1916</td></tr><tr><td>ламентация</td><td>Но… Впрочем, не стану я сегодня опять предаваться бесплодным ламентациям на эту печальную тему</td><td>1925</td></tr><tr><td>лганье</td><td>До чего доходит лганье газет</td><td>1917</td></tr><tr><td>лганьё</td><td>Оказалось, что «все в порядке»: та же система обманов, неуважения к личности и лганья, лганья, лганья</td><td>1931</td></tr><tr><td>легкомыслие</td><td>Вот и расплачивайся теперь имениями и фабриками за свое легкомыслие</td><td>1917</td></tr><tr><td>логовище</td><td>Однако птицы улетают из своих гнезд, и маленькие звери покидают свои логовища, и пауки улетают на своей паутине</td><td>1938</td></tr><tr><td>лодырничать</td><td>Придется теперь до Рождества лодырничать</td><td>1917</td></tr><tr><td>ломака</td><td>Он очень прост, не ломака, искренен, весь на ладони</td><td>1923</td></tr><tr><td>льнуть</td><td>Он льнет все к Сомову, а тот довольно жестоко с ним обращается</td><td>1921</td></tr><tr><td>любезно</td><td>Он меня очень любезно принял; вынул золотой портсигар, угостил папироской</td><td>1922</td></tr><tr><td>любовность</td><td>Для меня он становится все милее, и все более я чувствую к нему нежность и любовность</td><td>1908</td></tr><tr><td>магометанин</td><td>Я хотел писать магометанину и искал хорошие изречения, но не нашел</td><td>1908</td></tr><tr><td>мазурик</td><td>Можно людям не доверять, считать их мазуриками и ворами, но нельзя это им высказывать и озлоблять</td><td>1906</td></tr><tr><td>малодушествовать</td><td>Не говорил он тогда ненужных слов, не малодушествовал, держался крепко и спокойно попрощался</td><td>1914</td></tr><tr><td>малоинтеллигентный</td><td>Лицо его показалось мне грубоватым и малоинтеллигентным</td><td>1919</td></tr><tr><td>малосодержательный</td><td>Лекции о внешней политике и духовной жизни — самые неудачные из всех моих костромских лекций: нудно, мертво и дробно, а последняя — даже малосодержательна</td><td>1921</td></tr><tr><td>малоуспешный</td><td>Наша квартира полнехонька клопами, с которыми энергично, но малоуспешно боремся</td><td>1930</td></tr><tr><td>малоутешительный</td><td>Хвостов рассказывает о Казани малоутешительные вещи</td><td>1911</td></tr><tr><td>манифестант</td><td>Сверх ожидания, манифестация не удалась: народа набралось порядочно, но — или не было главарей, или духа не хватило — всесословная сходка эта тянулась вяло и тихо; полиции видно не было, никто никого не трогал, и манифестанты чувствовали себя довольно глупо</td><td>1904</td></tr><tr><td>манкировать</td><td>Этот больной человек, все время манкирующий службой, нашел натуральным сам просить о своем повышении — довольно-таки бесцеремонно</td><td>1911</td></tr><tr><td>меблирашка</td><td>Меблирована она была дёшево, но с какими-то тканями, ковриками, словом, художническая богема, что, впрочем, лучше, чем трафарет обыкновенных меблирашек</td><td>1926</td></tr><tr><td>меблированный</td><td>Сегодня я совершил с ним прогулку в Курорт, где он пытался найти меблированную комнату для своей жены с десятимесячным сыном, однако безуспешно: никто не хотел сдавать комнату из-за ребенка</td><td>1916</td></tr><tr><td>медленность</td><td>Мне казалось, что я могу победить свою трусость и медленность</td><td>1919</td></tr><tr><td>междоусобие</td><td>Русское общество все представляет Германию сильную, а не Германию униженную, побежденную, ослабленную междоусобием</td><td>1919</td></tr><tr><td>междуведомственный</td><td>Состоялось мое назначение председателем междуведомственной квартирной комиссии</td><td>1918</td></tr><tr><td>меланхолический</td><td>Все вылилось в нашем танце, вся мечтательная, нет, скорее меланхолическая русская душа</td><td>1916</td></tr><tr><td>меледа</td><td>Все-таки меледа с этими деньгами и скучно очень ждать их</td><td>1929</td></tr><tr><td>мертвить</td><td>Официальные казенные связи только мертвят, довершая распад</td><td>1940</td></tr><tr><td>местопребывание</td><td>Учреждения, музеи и институты постоянно меняют свое местопребывание, и даже уличные торговцы, которые в других краях держатся за определенное место, каждый день оказываются на новом месте</td><td>1926</td></tr><tr><td>мешочник</td><td>Явились мешочники из других губерний закупать муку, платят по 20 руб</td><td>1917</td></tr><tr><td>минование</td><td>По миновании этих обстоятельств минуют и порожденные ими меры</td><td>1934</td></tr><tr><td>мирволить</td><td>В письмах Государь сначала приказывал ему мирволить полякам и удалить от себя русских, в которых заметит нерасположение к полякам</td><td>1909</td></tr><tr><td>миросозерцание</td><td>Люди будущего — материалисты по миросозерцанию и лично — идеалисты</td><td>1915</td></tr><tr><td>многолюдство</td><td>Поехал в Зоологический сад (теперь его будут звать Зоологический парк) посмотреть тех зверей, которых в прошлый раз, за многолюдством, трудно было рассмотреть</td><td>1925</td></tr><tr><td>многоречивый</td><td>Это огромный храм с роскошно одетыми статуями и многоречивыми надписями</td><td>1907</td></tr><tr><td>молодечество</td><td>Я помню, на Кавказе считалось особенным молодечеством ездить ночью на дороги и там убивать встречных немирных татар</td><td>1906</td></tr><tr><td>молодцеватый</td><td>У казаков новые алые погоны, малиновые патронташи, винтовки, молодцеватые лица</td><td>1921</td></tr><tr><td>мотоциклет</td><td>Наш мотоциклет затарахтел по шоссе в поисках Штаба дивизии, с донесением о подходе к Пилькалену пехотного полка</td><td>1914</td></tr><tr><td>мотоциклетка</td><td>При прощании, я у него выпросил бидон бензина для эскадронной мотоциклетки</td><td>1914</td></tr><tr><td>музейский</td><td>Мы долго бродили по музейским залам и о чем-то говорили</td><td>1923</td></tr><tr><td>мучительство</td><td>Пора бы мне начать тревожиться, но мне так надоел театр, что я сейчас готов даже нужды отведать, только бы меня не забрали для этого мучительства</td><td>1924</td></tr><tr><td>набавлять</td><td>Причиной этого явления отчасти уничтожение водки, а, главным образом, то, что мужик, следуя общему примеру, стал набавлять цену на продаваемый им товар: муку, масло, яйца, дрова</td><td>1916</td></tr><tr><td>наблюсти</td><td>Показывалась Ваша фигура — Ваши линии, так давно знакомые во всех мелочах, изученные, с любовью наблюденные</td><td>1902</td></tr><tr><td>надерзить</td><td>Мама вчера утром на меня рассердилась, потому, что я надерзила, и простила только около завтрака</td><td>1916</td></tr><tr><td>надписать</td><td>Я сам надписал адрес в Петербург, в канцелярию Кавалергардского полка</td><td>1910</td></tr><tr><td>надсаживать</td><td>Они играют со всем: с каждой лужей, бугорком, забором, поворотом, проезжими санями, а я только ору, надсаживая горло</td><td>1919</td></tr><tr><td>надуматься</td><td>Слава богу, надумались хотя убрать с правого фланга сборные части 7-й дивизии, а оставляют там, кажется, две цельные роты 28-го полка и две роты охотников</td><td>1904</td></tr><tr><td>назади</td><td>По общей тропе идут люди разных лет и разных сил, один впереди, другой далеко назади</td><td>1935</td></tr><tr><td>наклеветать</td><td>Седова еще наклеветала на Андрианова, будто до нее дошли слухи, что он ее уронит</td><td>1907</td></tr><tr><td>накрапывать</td><td>Когда мы только что пришли в МХАТ — началась гроза, а уходили — накрапывал дождь</td><td>1939</td></tr><tr><td>наново</td><td>Все наново выкрашено и тонко продумано</td><td>1918</td></tr><tr><td>наперерыв</td><td>Хозяева харчевен наперерыв зазывают к себе, да так энергично, что первое время кажется, что нас отчаянно ругают</td><td>1907</td></tr><tr><td>напечатание</td><td>Боюсь, что дело с помещением в «Комсомольской правде» портретов уволенных сотрудников и с напечатанием грязной статьи о них кончится ссорою между ячейкой и другими</td><td>1929</td></tr><tr><td>напортить</td><td>Новосильцева много себе напортила своей мелочностью: она постоянно отвлекалась от темы, начинала вспоминать разные ничтожные обстоятельства и этим раздражала директора</td><td>1910</td></tr><tr><td>напускаться</td><td>Он вошёл совершенно в ярости: бледный со сверкающими глазами, и напустился на бедного Кулика, который сидел около двери</td><td>1939</td></tr><tr><td>напуститься</td><td>Там он, говорят, первым долгом грубо напустился на командира батареи, капитана В</td><td>1904</td></tr><tr><td>напутственный</td><td>Вечером приходил прощаться Хелуанский иеромонах, покропил нашу комнату св водой и прочел напутственную молитву</td><td>1913</td></tr><tr><td>наружность</td><td>По-видимому, простая и милая, не красивой, но привлекательной наружности, рожденная Насакина, дочь Симбирского помещика, она мне понравилась</td><td>1906</td></tr><tr><td>насесть</td><td>Дорогой большой шарабан, куда насело семеро кадет, в одном месте опрокинулся, но все обошлось благополучно</td><td>1910</td></tr><tr><td>насилу</td><td>Часов в 12 полил такой дождь, что через 10 минут насилу вытаскивали ноги из грязи</td><td>1905</td></tr><tr><td>насказывать</td><td>Окрестности красивы, но про них столько насказано, что ждёшь больше</td><td>1920</td></tr><tr><td>насупротив</td><td>Целью нашей поездки оказался вовсе не Мариинский дворец, а здание Министерства внутренних дел (точнее, Совета этого министерства), находящееся за Александрийским театром, насупротив здания Театральной дирекции</td><td>1917</td></tr><tr><td>натянутость</td><td>Сухо, с опаской принимают каждое слово, есть натянутость к нам</td><td>1935</td></tr><tr><td>начальнический</td><td>Вообще хамоватые начальнические ноты у него проявляются все чаще, но не ко мне</td><td>1921</td></tr><tr><td>начальствующий</td><td>Появление большого числа орудий крупного калибра сильно ухудшило наше положение, но все-таки после совещания с главными начальствующими лицами я отдал приказание держать позиции</td><td>1904</td></tr><tr><td>начатие</td><td>Нужно заметить, однако, что это от первого, якобы православного христианина, встречаю такую неделикатность со времени начатия войны</td><td>1904</td></tr><tr><td>начерно</td><td>Закончил (начерно) рассказ, перечитал и подумал о копии с «Первой любви» — тот же героизм, тот же конец, правда, с меньшей долей сахарина</td><td>1929</td></tr><tr><td>неблагонадежность</td><td>Уволили с пометкой в паспорте о неблагонадежности</td><td>1936</td></tr><tr><td>невзнос</td><td>Сидим без электрического освещения: закрыто за невзнос домоуправлением платы за электричество, слышал, что многие квартиранты не платили по несколько месяцев</td><td>1929</td></tr><tr><td>невыдержанность</td><td>Уж больно много у нас еще дикости и невежества, порывистости, невыдержанности, необдуманности</td><td>1917</td></tr><tr><td>невылазный</td><td>Мостовых нет, в сухую погоду пыль слепит глаза, в сырую — грязь невылазная</td><td>1909</td></tr><tr><td>невыясненность</td><td>Писать для газеты я не в состоянии из-за полной невыясненности общего положения</td><td>1917</td></tr><tr><td>недвижно</td><td>В театре в партере сижу одна, недвижно, ни разу не сойдя с места</td><td>1937</td></tr><tr><td>недоброжелательство</td><td>Сколько стало во мне злобы, недоверия, недоброжелательства</td><td>1928</td></tr><tr><td>недокончить</td><td>Окопы, которые мы заняли, были недокончены, а потому нам было приказано удлинять и уширять их</td><td>1915</td></tr><tr><td>недохват</td><td>Сырья у нас много, значит, недохват его выработки, значит, недохват рабочего, недохват рабсилы</td><td>1930</td></tr><tr><td>недохватка</td><td>Нет, здесь речь идет об абсолютной недохватке топлива и съестных припасов</td><td>1915</td></tr><tr><td>недреманный</td><td>Сама матушка среди своих детей и квартирантов — командир, недреманное око; видимо, нежно любит детей и сама пользуется такой же любовью</td><td>1931</td></tr><tr><td>недурно</td><td>Недурно оценивает свой труд пролетариат</td><td>1919</td></tr><tr><td>нездоровье</td><td>Но на работоспособности нездоровье отражается плохо</td><td>1910</td></tr><tr><td>незнакомство</td><td>Первый раз в Эрмитаж приходит Мотя, которую я пристыдил за ее незнакомство с музеем</td><td>1924</td></tr><tr><td>неизъяснимый</td><td>В ее фигуре царское величие, в ее движениях неизъяснимое очарование, грация и в то же время простота</td><td>1914</td></tr><tr><td>неинтеллигентный</td><td>Он не глуп, но очень темен и попросту неинтеллигентен</td><td>1937</td></tr><tr><td>неистовствовать</td><td>Беляки чувствовали, что им приходит конец, а потому и неистовствовали, как дикие звери</td><td>1919</td></tr><tr><td>нейти</td><td>Сомова не было, это мило: к себе не зовет, сам нейдет</td><td>1907</td></tr><tr><td>некультурность</td><td>До каких пределов разрушения дошел Петроград, до какой степени некультурности дошла наша жизнь – «этого ни пером описать, ни рассказать»</td><td>1922</td></tr><tr><td>неладно</td><td>Я ему сказала, что заметила, как волосы точно передают состояние здоровья и настроения человека, — тускнеют, секутся, грубеют, мертвеют, когда в организме что-нибудь неладно; упружатся, блестят, становятся эластичны, когда обмен веществ хорош</td><td>1926</td></tr><tr><td>нелады</td><td>Он хорошо осведомлен о крупных неладах между главнокомандующим армиями Северо-Западного фронта Жилинским и подчиненным ему командующим 1-ой армиею Ренненкампфом</td><td>1914</td></tr><tr><td>нелюбезно</td><td>Жиличка, красноносая хитрая на вид баба, встретила меня нелюбезно</td><td>1924</td></tr><tr><td>немирный</td><td>Но мир есть высшее сокровище души; душа немирная не может любить и хвалить Бога</td><td>1925</td></tr><tr><td>немолчный</td><td>Прошлогодняя стрельба казалась пустяком по сравнению с немолчным гулом этого дня</td><td>1919</td></tr><tr><td>ненастье</td><td>Проблеск тепла и солнечных лучей после затяжного ненастья</td><td>1918</td></tr><tr><td>необычайность</td><td>Вокзал грязен, переполнен до необычайности</td><td>1919</td></tr><tr><td>неодобрительно</td><td>Вообще, очевидно, Хрещатицкий и Ринов чего-то не поделили и это и дает первому повод резко и неодобрительно отзываться о втором</td><td>1919</td></tr><tr><td>неопределенно</td><td>Борьба затягивается и делается все неопределенней и безнадежней, истощая последние экономические силы России</td><td>1919</td></tr><tr><td>неопределимый</td><td>Потому-то и чудо, что незнаемое, неопределимое</td><td>1920</td></tr><tr><td>неослабный</td><td>Неослабно точит меня забота о судьбе моих ребятишек, которых так хотелось бы видеть устроенными, пока я жив</td><td>1917</td></tr><tr><td>неотвязчивый</td><td>Спасибо ей за ту почти неотвязчивую заботу, которой она постоянно окружала своего великого мужа</td><td>1930</td></tr><tr><td>неотступный</td><td>Я не хочу допустить в свою душу все те поздние сожаления — а что-то назойливо, неотступно день и ночь, день и ночь «гложет»</td><td>1935</td></tr><tr><td>неподобный</td><td>Даруй, Господи, мне сил и решимости так же воздерживаться и от душевных неподобных привычек</td><td>1936</td></tr><tr><td>непокойно</td><td>Как подобает истинно русским людям и христианам, первая моя забота была украсить свою походную храмину иконами: без них как-то неуютно и на душе непокойно</td><td>1904</td></tr><tr><td>непосланный</td><td>Набросок непосланного письма к Л</td><td>1902</td></tr><tr><td>неприбытие</td><td>Назначенное на сегодня в Музее заседание моего разряда за неприбытием Анисимова и Клейна не состоялось</td><td>1924</td></tr><tr><td>неприкрашенный</td><td>Эх, погана наша российская неприкрашенная действительность</td><td>1916</td></tr><tr><td>неприложимый</td><td>Я имею свои убеждения, но в данных условиях они, мне кажется, неприложимы к действительности</td><td>1916</td></tr><tr><td>неприятель</td><td>Вечером наша рота выступила на поддержку одной роты нашего батальона и, отбив неприятеля, возвратились к 10 вечера</td><td>1915</td></tr><tr><td>непробудный</td><td>Во имя Господа, умоляю Вас, не дайте совести Вашей окончательно заглохнуть — заснуть непробудным сном, ведущим только к смерти, от которой уже никто и ничто не сможет Вас пробудить, но до которой в беспредельной любви своей Христос Спаситель Ваш не захочет Вас допустить</td><td>1910</td></tr><tr><td>непротивление</td><td>Надо понять, как горячо и убежденно этот человек четверть столетия проповедовал на весь мир не только ненужность власти и необходимость непротивления, но и практическую легкость обращения человека при каких угодно условиях жизни в христианского анархиста</td><td>1907</td></tr><tr><td>непрочность</td><td>Но во всем какая-то лихорадочность и непрочность</td><td>1937</td></tr><tr><td>нерадение</td><td>Случается, что за нерадение или другие отрицательные стороны, многих отправляют обратно, восвояси, требуя лучших заместителей</td><td>1908</td></tr><tr><td>нерасположение</td><td>Устаревший автор всю вину валит на меня, обвиняя будто в личном к нему нерасположении</td><td>1907</td></tr><tr><td>нервить</td><td>Ежеминутные остановки поезда с продолжительными стояниями нервят, раздражают</td><td>1915</td></tr><tr><td>несимпатичный</td><td>Пришел в ресторан брат старухи, несимпатичного вида</td><td>1925</td></tr><tr><td>нескладица</td><td>Внешние препятствия ясны, но не ими одними объясняется наша нескладица</td><td>1919</td></tr><tr><td>нескладно</td><td>Даже на речи моей отражается — говорю неуверенно, даже несколько нескладно, а смеюсь совсем ненатурально — тихо и точно сдавленно</td><td>1940</td></tr><tr><td>несмолкаемый</td><td>Проснувшиеся птицы и тысячи всяких насекомых наполняют его своей бодрой и несмолкаемой трескотней</td><td>1914</td></tr><tr><td>несносный</td><td>Про Божовского он тоже меня расспрашивал и говорил, что в общем Божовский пользуется симпатиями, хотя все упрекают его за несносный характер и за постоянный ответ «это невозможно»</td><td>1911</td></tr><tr><td>несомненность</td><td>Мандельштам говорил со слишком большой несомненностью, и положение его казалось действительно критическим</td><td>1927</td></tr><tr><td>несообразность</td><td>И вот он теперь рассказывает им, что я говорил, что в Бога верить не надо, и еще разные несообразности</td><td>1907</td></tr><tr><td>несосветимый</td><td>Ведь вся эта низость, вся эта несосветимая подлость, душащая, как азот, от неудачничества нашего</td><td>1918</td></tr><tr><td>нестерпимо</td><td>У Насоновых было много кадет и поэтому было весело, но моментами делалось как‑то нестерпимо грустно</td><td>1924</td></tr><tr><td>нестройно</td><td>Как-то шли нестройно, подчеркивающе демократично — не было строевого порядка, офицеры тушевались и, смешиваясь с солдатами, не давали нужного военного тона, одним словом, это уже было не войско, а толпа</td><td>1917</td></tr><tr><td>несходный</td><td>А вот характеры у них несходные</td><td>1918</td></tr><tr><td>несчастливец</td><td>Есть зато и несчастливцы — это те, кто или не получил писем, или же получил, но тревожные</td><td>1914</td></tr><tr><td>нетактичный</td><td>Гущин, как всегда, вел себя крайне нетактично и сказал опять им обоим, что они не могут понять то, что он преподает</td><td>1904</td></tr><tr><td>неудовлетворительность</td><td>Сперанский сообщил мне, что он частным образом сказал Шамбинаго о неудовлетворительности его диссертации и посоветовал ее переработать</td><td>1913</td></tr><tr><td>неудовольствие</td><td>Я боюсь, что это решение вызовет большое неудовольствие</td><td>1905</td></tr><tr><td>неумолчный</td><td>Ночи здесь великолепные, засыпаю я под перекликание соловьев и неумолчный страстный хор лягушек, которых здесь необычайно много</td><td>1927</td></tr><tr><td>неуспех</td><td>Причины неуспеха в войне, говорили, в том, что не действовали вместе части войск (их начальники), была плохая организация</td><td>1905</td></tr><tr><td>неустройство</td><td>Столько неустройств, невзгод у самой</td><td>1930</td></tr><tr><td>неутешный</td><td>Очень плакала, горе неутешное и ничего непоправимо</td><td>1911</td></tr><tr><td>нехолодный</td><td>Снег, лежавший ночью, стаял, и день простоял нехолодный</td><td>1912</td></tr><tr><td>нечитанный</td><td>Дело в том, что тогда весною, вскоре после последних записей, мне понадобилось, было для меня нужно (почему — на этой странице нельзя объяснять) дать прочесть эту нечитанную, нераскрываемую тетрадь Философову</td><td>1904</td></tr><tr><td>нечуткость</td><td>Он очень захотел ее увидеть, упрекал меня в черствости, нечуткости</td><td>1931</td></tr><tr><td>новокуплять</td><td>Мы плавали на остров на новокупленной лодке</td><td>1924</td></tr><tr><td>обалделый</td><td>В одном месте забыл ключи от своего номера гостиницы, в другом кашне, измученный, обалделый старик</td><td>1928</td></tr><tr><td>обвевать</td><td>Дух вражды, непонимания, грубости и часто прямой и загадочной ненависти, которым обвеян я был со всех сторон, насторожил душу на темные и страшные ожидания</td><td>1918</td></tr><tr><td>обезличиваться</td><td>Не всякий может перенести такую ширину возможностей, атрофируется инстинкт формообразования, человек утомляется и обезличивается</td><td>1923</td></tr><tr><td>обессиление</td><td>В Москве я все время готовился к отъезду и столь интенсивно, что не мог уже ни в театр ходить, ни уделять достаточно времени жене и детям, иногда день кончался полным обессилением и каким-то внутренним разломом</td><td>1934</td></tr><tr><td>облобызаться</td><td>При расставании подвернулся поп, с которым тоже облобызался</td><td>1916</td></tr><tr><td>обложной</td><td>Погода испортилась — стало сразу холодно, полил обложной осенний дождь</td><td>1904</td></tr><tr><td>обманно</td><td>Была теплая, обманно весенняя погода, навевающая истому и оживление вместе, любовь и меланхолию</td><td>1906</td></tr><tr><td>обнажение</td><td>Особенно памятным осталось урочище Хунцыл, где скалы, сложенные из гигантских обнажений (местами матрасовидных), и среди скал валяющиеся, также гигантские, отгорженцы привлекли внимание нашего геолога</td><td>1908</td></tr><tr><td>обновленческий</td><td>Всего неделю занимался подборкой материалов для записки об обновленческом движении в Петрограде</td><td>1924</td></tr><tr><td>обноситься</td><td>Особенно ценное для солдат белье, так как они сильно обносились, двигаясь между скал и кустов</td><td>1904</td></tr><tr><td>ободрять</td><td>Государь несколько раз меня ободрял и поздравлял, что все для меня так хорошо окончилось</td><td>1914</td></tr><tr><td>обождать</td><td>Довольно малейшего шума, крика, чтобы произвести панику в этой нервной зале, а потому лучше обождать</td><td>1905</td></tr><tr><td>оборвыш</td><td>Куган начал свой путь обогащения с беспризорного оборвыша, а сейчас здоровенный верзила 23-х лет имеет миллион долларов в кармане чистоганом</td><td>1937</td></tr><tr><td>обрисовка</td><td>Скучно; художественной обрисовки характеров нет; действующие лица — марионетки</td><td>1920</td></tr><tr><td>обсушиваться</td><td>Обсушившись около костра, я пошел по тропе дальше</td><td>1935</td></tr><tr><td>обтерпеться</td><td>Пассажиры говорят: «Народ обтерпелся, на войну идут — не так плачут, как раньше»</td><td>1916</td></tr><tr><td>обтрепанный</td><td>К ней подошел какой то обтрепанный тип и спросил ее о причине слез</td><td>1934</td></tr><tr><td>общежитейский</td><td>Мелочи общежитейские, но не от них какое-то встревоженное, выводящее из себя ожидание страха перед чем-то, что будет</td><td>1934</td></tr><tr><td>объемистый</td><td>Очередь в буфет была огромная, и хвост перед столом с объемистым самоваром приводил в уныние, а когда очередь дошла до меня, самовар оказался пустым, и его унесли ставить опять, не заменив другим</td><td>1919</td></tr><tr><td>обывательщина</td><td>Тонкий, изысканный вкус, органическое отвращение ко всякой пошлости и обывательщине, безупречная честность, высокая интеллигентность и культурность, умение очаровать слушателя с первых слов или самому слушать — вот каков мне представляется сейчас отец</td><td>1932</td></tr><tr><td>обыденщина</td><td>В нём былом много гадкого, но не было пошлости, обыденщины, он любил меня одну минуту, но любил красиво, с уважением, ни разу не унизил меня, пока любил, я это чувствовала</td><td>1912</td></tr><tr><td>обыкновенно</td><td>Мысль и любовь редко в дружбе живут, обыкновенно мысль разрушает — одно дело, любовь создает — совершенно другое</td><td>1918</td></tr><tr><td>оглаживать</td><td>Единственный способ общения с ними, очень утомительный — это постоянно оглаживать их, нянчиться с ними</td><td>1918</td></tr><tr><td>огулом</td><td>Забирали их огулом, не всегда точно проверив, есть ли на деле наличие кулачества</td><td>1930</td></tr><tr><td>одичание</td><td>Лазарев: человечество идет к одичанию</td><td>1938</td></tr><tr><td>однозвучный</td><td>В дороге он у нас исполняет роль повара и частенько готовит нам блюдо, однозвучное с его фамилией</td><td>1935</td></tr><tr><td>одурелый</td><td>Я с трудом, чувствуя себя совершенно оглохшей и одурелой, добралась до кровати и почти упала на нее, и в это время сердце так бешено колотилось; что я слышала его удары и ощущала сильную боль в груди</td><td>1920</td></tr><tr><td>одушевлять</td><td>Передайте всем председателям отделов и всем членам Союза русского народа, приславшим мне изъявления одушевляющих их чувств, мою сердечную благодарность за их преданность и готовность служить Престолу и благу дорогой родины</td><td>1907</td></tr><tr><td>одушевляться</td><td>Разве разум не может быть нежным, восприимчивым к движениям сердца, к впечатлениям красоты и величия, а чувство не может согласоваться с разумом, им освещаться и одушевляться</td><td>1917</td></tr><tr><td>оживленно</td><td>По вечерам сидели и болтали очень оживленно о путешествиях, о науке, и даже мы с Борзовым в последний день вывернули старые музейские сплетни</td><td>1920</td></tr><tr><td>озверение</td><td>Я совершенно не понимаю и страдаю ужасно, когда слышу об озверении людей и бесконечной войне</td><td>1905</td></tr><tr><td>озлиться</td><td>Петр, по натуре очень мирный человек, озлился наконец, испортился характером, так что оставлять его дольше в Хакодате было бы обрекать Церковь на безнадежное состояние и губить его самого</td><td>1900</td></tr><tr><td>озлобление</td><td>Он заговорил о стражниках и об озлоблении, которое они вызывают в народе, и удивлялся сравнительной миролюбивости народа</td><td>1909</td></tr><tr><td>озорство</td><td>Кшесинская через Великого Князя просила передать, что если возьмут Андрианова, она вообще отказывается от дальнейших гастролей и бенефисного спектакля Гердта, что, вероятно, Дирекция не заставит ее привести в исполнение свое решение — вот до какого озорства дошла эта особа</td><td>1910</td></tr><tr><td>озябнуть</td><td>Сидели на террасе Лена, Нюлька и я, но потом озябли и ушли в комнаты</td><td>1906</td></tr><tr><td>окаянство</td><td>Я благодарил ее за то, что она облагодетельствовала всю жизнь мою и просил простить меня за мое окаянство</td><td>1924</td></tr><tr><td>окладной</td><td>Весь день поливает окладной дождь</td><td>1931</td></tr><tr><td>околичность</td><td>Основываясь на «регламенте», он без околичностей заявляет, что Мамантов не имел права давать ход жалобе оскорбленной жены без его, старого бесстыдника, согласия</td><td>1917</td></tr><tr><td>опоэтизировать</td><td>Хотя все у меня расписано и размерено — по-старому, по-студенчески, но я охотно пожертвую двумя неделями моих систематических занятий, чтобы окунуться в новый мир, свежий и обновляющий, красивой опоэтизированной старины</td><td>1921</td></tr><tr><td>опрометью</td><td>Сразу паника передалась на площадь Эспанья, оттуда кто-то брякнул по телефону на улицу Алкала, и вот из штаба, сбивая друг друга с ног, побежали опрометью люди</td><td>1936</td></tr><tr><td>опроститься</td><td>Драное белье, ветхое платье, башмаки без подошв — так опростились мы до идеального первобытного состояния, чтобы стать счастливыми по новейшим рецептам</td><td>1920</td></tr><tr><td>опрощение</td><td>И вот когда это — я сам, или сама — исчезнет, то сор, и пепел, и спальня — все это внезапно делается чужим и внушает сначала раздраженье, потом опрощение и невыносимость бытия</td><td>1919</td></tr><tr><td>оптимистически</td><td>Он был очень доволен и очень оптимистически настроен</td><td>1915</td></tr><tr><td>опустелый</td><td>Возвращаясь в посольство опустелыми аллеями Летнего сада, я говорю себе, что я, может быть, был только свидетелем самых знаменательных фактов современной истории</td><td>1917</td></tr><tr><td>оратель</td><td>Нужно только сделать все, чтобы в новый орган попали настоящие, деловые представителя Сибирского населения, а не наезжие оратели, как то бывало в Сибири при выборах в Государственную Думу и Учредительное Собрание</td><td>1919</td></tr><tr><td>ораторствовать</td><td>Ораторствуют на митингах, главным образом сербы, которые не дурно говорят по-русски</td><td>1918</td></tr><tr><td>оркестрант</td><td>На «Аиду» времени не осталось, ибо оркестранты, играющие в кинематографах и прочих весёлых местах, стали просить кончить репетицию</td><td>1913</td></tr><tr><td>осведомительный</td><td>Напрасно мы, члены комиссии, уверяли, что комиссия эта — сугубо временная, подготовительная, осведомительная</td><td>1917</td></tr><tr><td>осведомление</td><td>Единственный широкий и наиболее надежный источник осведомления — печать — лишена возможности, с одной стороны, получать интересные, но совершенно верные сведения, с другой — предавать их гласности</td><td>1915</td></tr><tr><td>осведомляться</td><td>Я осведомился, с кем имею дело</td><td>1909</td></tr><tr><td>освидетельствовать</td><td>Он внимательно освидетельствовал меня и сказал, что мне надо удалить одиннадцать зубов</td><td>1934</td></tr><tr><td>осердиться</td><td>Осердился, плюнул, перестал ходить, звонить, справляться: будь что будет</td><td>1923</td></tr><tr><td>оскандалиться</td><td>Странно видеть напечатанной свою фамилию и немного страшновато — не оскандалиться бы</td><td>1910</td></tr><tr><td>осунуться</td><td>Он похудел, как-то осунулся весь</td><td>1939</td></tr><tr><td>осьмушка</td><td>В данный момент не имею ни копейки денег, не на что бывает купить осьмушку табаку или пачку папирос</td><td>1933</td></tr><tr><td>осязательный</td><td>Приведенные выше факты, касающиеся двух стран, свидетельствуют, что эти результаты уже начали принимать осязательную форму</td><td>1930</td></tr><tr><td>отбитие</td><td>Большую помощь при отбитии атаки красных оказал бронепоезд “Дроздовец”, который врезался в цепи красных</td><td>1919</td></tr><tr><td>отвечивать</td><td>На это было отвечено строго и внушительно, чтобы и думать не сметь</td><td>1917</td></tr><tr><td>отвиливать</td><td>Он от работы отвиливает и, где надо, не только не помогает, но прямо портит</td><td>1908</td></tr><tr><td>отвлеченно</td><td>Да, денег и труда жалко, но как-то отвлеченно</td><td>1918</td></tr><tr><td>отвлеченность</td><td>— Может быть, настоящее откровение любви только через страдание, через трагедию и приходит, — сказала я и сама боялась, что это слишком напыщенно, но его облегчала отвлеченность и слов, и мысли</td><td>1913</td></tr><tr><td>отвоз</td><td>Из тюрьмы известия все мрачные: оказалось, что в пятницу не приняли принесенного и для Нади, и для Фроси, которая там тоже сидит с момента отвоза детей, сидит как свидетельница по делу Володи</td><td>1921</td></tr><tr><td>отворять</td><td>Она смотрит ласково, ласково, когда отворила дверь и осветила меня улыбкой</td><td>1918</td></tr><tr><td>отвычка</td><td>Работают они довольно аккуратно, но сильнейшим образом сказывается отвычка от умственного труда</td><td>1921</td></tr><tr><td>отгрызаться</td><td>Он отгрызался от всех обвинений и даже утайку партийных взносов представил в таком виде, как будто ее вовсе и не было</td><td>1929</td></tr><tr><td>отдание</td><td>Не явившимся — лишение семян для посева, усиленная трудовая повинность, отдание под суд и прочие наказания</td><td>1920</td></tr><tr><td>отдумать</td><td>Собирался потом в музей, но отдумал и поехал домой</td><td>1924</td></tr><tr><td>отдуть</td><td>После рассказа Пети я спрашиваю: — Почему же ты, сильный человек, не взял этого человечка за шиворот и не отдул его по щекам</td><td>1928</td></tr><tr><td>отзванивать</td><td>Я предпочла отменить светский образ жизни и сейчас же отзвонила, кому следовало, и только вечером будет у нас около 8 часов Лалиберте, который должен был прийти уже в 5 часов вечера</td><td>1929</td></tr><tr><td>отзвук</td><td>Между нами протянулась невидимая нить — в тоне и словах моего ответа я сумел передать ему мое сочувствие, мой отзвук на его горе</td><td>1910</td></tr><tr><td>отколачивать</td><td>Ходьба на лыжах основательно изматывает, ибо днем снег мягкий, нога уходит глубоко, к унтам пристают комья снега, которые примерзают к лыжам, и их приходится то и дело отколачивать молотком</td><td>1936</td></tr><tr><td>отлагать</td><td>Пришёл домой и стал писать дневник; письма отлагаю до завтра</td><td>1900</td></tr><tr><td>отлучка</td><td>После трехдневной отлучки (поездка в Сростки) дел дома по хозяйству накопилось очень много</td><td>1927</td></tr><tr><td>отмежевываться</td><td>Отмежеваться от внешнего мира, уйти целиком во внутренний (книга, писанье, рисованье, рукоделие с мечтой пополам, беседа, если есть собеседники)</td><td>1935</td></tr><tr><td>отобрание</td><td>Полная покорность, полное разорение, отобрание хлеба и вещей</td><td>1918</td></tr><tr><td>отпечатание</td><td>Из Святейшего Синода получено 4000 на отпечатание и переплет 600 экземпляров Октоиха</td><td>1909</td></tr><tr><td>отрекомендовываться</td><td>Я — скульптор Карсинский, — отрекомендовался он, протягивая руку и широко, добродушно улыбаясь</td><td>1901</td></tr><tr><td>отсоветовать</td><td>Хочет уходить из МХАТа, мы отсоветовали ему</td><td>1939</td></tr><tr><td>отступя</td><td>Когда я сделал поклон, государь сказал: «Христос воскресе» и троекратно поцеловались, потом поклон и поцелуй ручки у императрицы Марии Федоровны; несколько отступя стояла императрица Александра Федоровна у стола, окруженная свитой, и после поцелуя ручки давала каждому яйцо; я получил из серой яшмы</td><td>1907</td></tr><tr><td>оттягать</td><td>Ушел бы с Рабфака совсем, но боюсь: он давно покушается на все здание училища, и если оттягает их, то важно служить на Рабфаке, — меньше шансов быть изгнанным из логовища</td><td>1923</td></tr><tr><td>отуманивать</td><td>Ничто не отуманивает так людей, как успех</td><td>1908</td></tr><tr><td>отчеркивать</td><td>Сегодня я купил том Шуберта с бездной вальсов и проиграл часть их, отчёркивая карандашом то, что нравится</td><td>1920</td></tr><tr><td>отчетливость</td><td>Он поставлен Романовым прекрасно, но так как мы получили декорации за 4 часа до спектакля, то отчетливость исполнения исчезла, и все получилось сбитым, смешанного характера</td><td>1914</td></tr><tr><td>отчужденность</td><td>Если у немцев железная дисциплина, то и у англичан она не меньше, но нет отчужденности солдат от офицеров</td><td>1919</td></tr><tr><td>отымать</td><td>Мы любим кровью, и кровь наша мучается, когда измена, охлаждение, разлука или смерть отымает от нас любимого</td><td>1934</td></tr><tr><td>отыскание</td><td>Меня не видел, по-видимому, был занят отысканием места для собственной персоны</td><td>1930</td></tr><tr><td>отыскивание</td><td>Щавинский говорил, что он занят отыскиванием по всему дворцу кухонь подальше от нашей столовой — вот и отлично</td><td>1918</td></tr><tr><td>отяжелеть</td><td>Внешне отяжелела и разрыхлилась плоть</td><td>1933</td></tr><tr><td>охорашиваться</td><td>Вообще город сильно принялся охорашиваться</td><td>1903</td></tr><tr><td>очутиться</td><td>После ашхабадского пекла мы внезапно очутились в раю</td><td>1927</td></tr><tr><td>ощутительный</td><td>Некоторое прискорбие вызывают комары, которые появились уже в ощутительном количестве</td><td>1936</td></tr><tr><td>палаточка</td><td>Заберешься в свою уютную палаточку, уничтожишь забравшуюся туда крылатую нечисть и некоторое время чувствуешь себя каким-то утихомиренно-опустошенным</td><td>1936</td></tr><tr><td>пальба</td><td>Пальба затихла, кучка стала расходиться</td><td>1919</td></tr><tr><td>пансионер</td><td>К маме приходила одна мама Тарасова и предлагала своего сына, ученика Коммерческого училища в пансионеры</td><td>1908</td></tr><tr><td>парадировать</td><td>То заключение, которое я вынес на екатеринбургском смотру ударных частей Сибирской армии, оказалось вполне верным; эти отлично парадировавшие части разбежались при первом же столкновении с красными и почти сразу же прекратили свое существование</td><td>1919</td></tr><tr><td>партийность</td><td>Вероятно, там будут только те, которые обязаны там быть по своей партийности или по должности</td><td>1921</td></tr><tr><td>патетический</td><td>Однако доклад его на конференции, блестящий по форме, был целиком построен на горячей полемике с «германофилами» и патетическом призыве «устоять» на старых позициях</td><td>1918</td></tr><tr><td>паточный</td><td>Ни сахара, ни дров, ни паточных конфет к чаю</td><td>1919</td></tr><tr><td>пейзанин</td><td>Что совершенно нестерпимо — это наглость, с которой большевики обставили свою делегацию: эта бутафория якобы представителей от крестьян, рабочих и солдат (для крестьян нарочно выбрали этакого «пейзана» с длинною бородою, «седого, как лунь, и глупого, как бревно»)</td><td>1917</td></tr><tr><td>первенствующий</td><td>К тому же прямой расчет подсказывал Англии, что если дела пойдут, как шли до сих пор, то все планы Англии будут сталкиваться с противодействием французской политики, стремившейся играть первенствующую роль</td><td>1923</td></tr><tr><td>перебывать</td><td>Сегодня у меня был с 11½ часов до часу прием — перебывало около 28 человек, главным образом, по вопросам о призывах в войска, отсрочках, а также с жалобами на дороговизну жизни</td><td>1916</td></tr><tr><td>перебыть</td><td>Так вот даже елка, если перебыть под ней во время дождя, становится немного, как дом: не хочется выйти из-под нее, и кажется, если случится опять дождь, то такой удобной уже не найти</td><td>1927</td></tr><tr><td>пережитое</td><td>Кто же должен расплачиваться за всё пережитое тобой</td><td>1932</td></tr><tr><td>пережиться</td><td>Понемножку все сгладится, переживется, забудется</td><td>1926</td></tr><tr><td>перезабыть</td><td>Так давно забросил фортепианную игру, что из своего прежде весьма обширного репертуара все перезабыл</td><td>1912</td></tr><tr><td>перемениться</td><td>Люди все те же, все те же хижины, но как страшно переменился весь свет</td><td>1908</td></tr><tr><td>переменять</td><td>Почему-то в разных инстанциях лица, состоящие на советской службе, находят необходимым переменять свою фамилию</td><td>1920</td></tr><tr><td>переменяться</td><td>С утра было тягостно, чувствовалось, переменяется погода</td><td>1932</td></tr><tr><td>перемокать</td><td>Сам перемок, как черт</td><td>1925</td></tr><tr><td>переобременять</td><td>Он мне ответил, что очень бы хотел улучшить их материальное положение, но боится переобременить средства государственного казначейства; я разозлился и сказал, что эта боязнь доведет до того, что и казначейства не будет, да и боящихся тоже</td><td>1919</td></tr><tr><td>перепиться</td><td>Оказалось, малознакомые гости перепились и вели себя черт знает как</td><td>1909</td></tr><tr><td>перерешать</td><td>Но Самарин заявил, что для достоинства съезда не следует перерешать решенное 4 часа назад</td><td>1917</td></tr><tr><td>переряженный</td><td>Говорят, много переряженных в войсковые одежды полицейских</td><td>1917</td></tr><tr><td>переряживать</td><td>Уж очень мало в нем грозного — просто переряженный артист</td><td>1914</td></tr><tr><td>пересмеиваться</td><td>Он рассматривал какой-то спортивный журнал, пересмеивался с бортмехаником</td><td>1936</td></tr><tr><td>перестрадать</td><td>Но то, что я перестрадала за это время, не может сравниться ни с какой человеческой скорбью</td><td>1910</td></tr><tr><td>перетаскиваться</td><td>С одной стороны, вроде как и уехать хочется, а с другой хочется, чтобы было уже наконец выяснено окончательно мое положение, чтобы не перетаскиваться мне со своими вещами из одного конца района в другой</td><td>1935</td></tr><tr><td>перетолковывать</td><td>Не ужинать нельзя, непременно заметят, перетолкуют и невыгодно осмеют</td><td>1907</td></tr><tr><td>перетрусить</td><td>Никифор Иванович перетрусил — советовал Лили и Асе бежать</td><td>1917</td></tr><tr><td>переутомлять</td><td>Я стал худой, как мощи, да и не я один: все переутомлены непосильной работой</td><td>1919</td></tr><tr><td>перецеловаться</td><td>Я перецеловалась с тетей Нюшей, Марией Григорьевной, Каролиной Ивановной и Ольгой Ивановной</td><td>1918</td></tr><tr><td>перечет</td><td>Первое мая — день сбора наших сил, день перечета наших рядов</td><td>1917</td></tr><tr><td>перечувствовать</td><td>Я так хорошо провел эти две ночи, перечувствовал столько нового, что ради этого одного стоило бы дежурить</td><td>1913</td></tr><tr><td>печатание</td><td>Амфитеатров жаловался на нехватку денег, тем более что он готовится приступить к печатанию третьей книжки, которая должна выйти тиражом в 10000 экземпляров</td><td>1906</td></tr><tr><td>печатно</td><td>Соболевский высказывал печатно, что в ограблении кассы университета принимали участие студенты-старосты, что и подтвердилось</td><td>1910</td></tr><tr><td>пикироваться</td><td>Вдруг пришел Потемкин, невыспавшийся, проигравшийся накануне в карты, я поил его чаем, играл «Кармен», тихо и мирно беседовали, пока не пришли наши и Сережа стал пикироваться с Потемкиным</td><td>1907</td></tr><tr><td>писчий</td><td>Вечером приходил человек покупать писчую машину</td><td>1920</td></tr><tr><td>письмецо</td><td>Из дому я получил коротенькое письмецо, одно за весь год, из письма видно, что они разграблены и бедствуют, но все живы</td><td>1920</td></tr><tr><td>письмишко</td><td>Небольшое письмишко прислал деревенский друг Иван Зиновьевич Голиков</td><td>1919</td></tr><tr><td>письмоводитель</td><td>Кравченко Степан — бывший письмоводителем у пристава, затем урядником, выдавал коммунистов при Деникине</td><td>1920</td></tr><tr><td>пленяться</td><td>В окнах магазинов видятся с тротуаров такие вкусные вещи, которыми мы, бывало, пленялись у Елисеева или у Белова</td><td>1921</td></tr><tr><td>плутоватый</td><td>Из длинной речи в течение двух с чем-то часов у меня осталась только его очень плутоватая мордочка дьяка</td><td>1926</td></tr><tr><td>плутовать</td><td>Играя в карты, я пользовалась рассеянным состоянием Либермана и Елены и плутовала, как шулер</td><td>1915</td></tr><tr><td>побраниться</td><td>Причин к разводу у нее с мужем ни малейших, просто побранились, или, может быть, подрались, и она сама ныне ухмыляется с радости, отправляясь к мужу; дал ей нагоняй, и к мужу нужно послать другой</td><td>1900</td></tr><tr><td>повапленный</td><td>Не от бедности, выходит, мы страдаем, а от внутренней гнили; даже и честность у нас была принужденная, из-под палки, даже и по этой части мы были гробами повапленными</td><td>1919</td></tr><tr><td>повелено</td><td>На днях всем Романовым было повелено явиться к Урицкому — регистрироваться</td><td>1918</td></tr><tr><td>повивать</td><td>Внизу в парке, куда мы потом спустились, столетние каштаны, повитые плющом, много хвойных деревьев, и все это вместе с видом на реку дает чудный вид и отдых</td><td>1904</td></tr><tr><td>повидаться</td><td>Укоряю я себя иногда за одичалость: вечно обращаюсь с японцами и со своим делом, от русского общества отстал и не нравится мне оно — не хочется даже в Россию, чтобы повидаться с родными, с друзьями</td><td>1902</td></tr><tr><td>повскакать</td><td>Ночью совсем близко грохотали пушки, стрекотали пулеметы и ружья, в халупе все повскакали, натягивали сапоги, пан вздыхал, сжимал руки и вскрикивал «Боже коханый, Боже коханый»</td><td>1914</td></tr><tr><td>повторительный</td><td>Интересно, что может получиться от таких повторительных уроков, но факт, что мне доступнее стало разбираться при чтении французского текста</td><td>1934</td></tr><tr><td>погромыхивать</td><td>Сегодня погромыхивает гром, но стало еще труднее: парит</td><td>1927</td></tr><tr><td>погрубеть</td><td>Он загорел, несколько погрубел и стал похож на казака (внешностью и обращением)</td><td>1913</td></tr><tr><td>подбадриваться</td><td>Лишь на воле, среди полей и лесов, я успокоился, оживился и подбодрился</td><td>1903</td></tr><tr><td>подвигаться</td><td>Был в комиссии, члены комиссии отлынивают, и вообще дела комиссии подвигаются медленно</td><td>1900</td></tr><tr><td>подголосок</td><td>Константин — подголосок, но верный подголосок чьих-то широко воющих голосов, снизу и слева</td><td>1917</td></tr><tr><td>подготовление</td><td>Остальное свободное время до 5 часов я посвятил подготовлению к экзамену</td><td>1905</td></tr><tr><td>подготовлять</td><td>Плотники заготовляют материал для подъемной машины, печники, маляр и щекатур заканчивают свои работы в птичнике, 3 поденщика делают проруби и подготовляют ловлю рыбы</td><td>1914</td></tr><tr><td>подготовляться</td><td>Эти дни в Мадриде подготовлялась смена правительства</td><td>1936</td></tr><tr><td>поддразнивать</td><td>Мы с ней в очень милых отношениях, с оттенком игривости; она слегка кокетничает, а я ухаживаю и поддразниваю</td><td>1910</td></tr><tr><td>подернуть</td><td>Утром было чудное небо, темно-синее, подернутое серым, с желтою полосою на горизонте</td><td>1912</td></tr><tr><td>подзакусить</td><td>Ловля оленей — это нелегкая штука, и на нее уходит не менее 2—3 часов, так что за это время мы успели основательно подзакусить, приготовив жирный суп из вермишели</td><td>1935</td></tr><tr><td>подзаняться</td><td>По окончании испытания нам сказали, что назначенные призовые вознаграждения выдадут тогда, когда люди докажут на деле пользу своих знаний, а пока приказали подзаняться получше</td><td>1905</td></tr><tr><td>подлаживание</td><td>Доклад Вавилова интересный; режет ухо его подлаживание под материалистическую диалектику — количество переходит в качество и тому подобное</td><td>1932</td></tr><tr><td>подлаживаться</td><td>Газета, не стесняясь, прямо говорит, что театр должен подлаживаться под вкус публики и печати, которая в свою очередь составляет орган наших же артистов</td><td>1902</td></tr><tr><td>подле</td><td>Офицеры 3 эскадрона заказывают завтрак в кабачке, подле которого мы стоим, и предлагают и нам принять в нем участие</td><td>1914</td></tr><tr><td>подсвистывать</td><td>На любимых местах некоторых пьес она очень удачно подсвистывала</td><td>1903</td></tr><tr><td>подстыть</td><td>Сильный иней, подстыло, застыли пеньковые веревки и канаты</td><td>1930</td></tr><tr><td>подыгрываться</td><td>Конечно, вокруг принца целая орава недостойных людей: взяточников, проходимцев, карьеристов, изучивших его слабые стороны и подыгрывающихся под них</td><td>1916</td></tr><tr><td>подыматься</td><td>Вода подымалась с совершенно неожиданной быстротой</td><td>1924</td></tr><tr><td>подыскание</td><td>День весь провел только в ходьбе, и в подыскании лошади для привоза сена</td><td>1937</td></tr><tr><td>пожатие</td><td>Горячее пожатие руки, несколько приветственных взглядов, вот встреча с любимой единственной подругой</td><td>1930</td></tr><tr><td>пожня</td><td>Докосили пожню под Дубровой, заполосок под Шипишиной, заполосок у Дресвянка и заполосок по гладям и дома в огородце</td><td>1921</td></tr><tr><td>позаняться</td><td>Дома немного позанялся, но скоро пришлось идти к Сабурову, который зачастил своими приглашениями</td><td>1913</td></tr><tr><td>поздороветь</td><td>Пришел Андрюша из лагерей допризывной подготовки, загорел, поздоровел</td><td>1928</td></tr><tr><td>поздравитель</td><td>Владыка приехал первым в 4 часа, а потом до 7 шла непрерывная волна посетителей и поздравителей</td><td>1940</td></tr><tr><td>позондировать</td><td>Он пришел по поручению группы шведских лесоэкспортеров позондировать насчет возможности соглашения</td><td>1931</td></tr><tr><td>позумент</td><td>Но главный трюк заключался в четырех дворцовых лакеях, облаченных в новые, утвержденные Акцентром для музеев, ярко-красные с шитым золотом позументам ливреи</td><td>1923</td></tr><tr><td>покамест</td><td>Сейчас я еще покамест ничего не могу дополнить к моим предыдущим размышлениям об этой опере, поэтому я это сделаю лучше в следующий раз</td><td>1939</td></tr><tr><td>поканчиваться</td><td>Остается уложить немногое ценное остальное и будет все, вопрос покончится</td><td>1909</td></tr><tr><td>покойно</td><td>В городе все покойно, и жизнь течет нормально</td><td>1914</td></tr><tr><td>покривиться</td><td>Но теперь, когда плиты покривились, побились и повывалились, когда одни плиты провалились в землю, а другие выскочили наружу, эта славная Мандаринская дорога скорее напоминает каменное решето, чем дорогу</td><td>1900</td></tr><tr><td>полгоря</td><td>Церковь от государства отделена — это еще полгоря; но люди-то государства отделились от нее — вот в чем безмерное горе</td><td>1919</td></tr><tr><td>полдневный</td><td>Китайские солдаты, наконец, кончили свой полдневный чифань и высыпали на стену, чтобы рассмотреть появившегося заморского всадника</td><td>1900</td></tr><tr><td>политиканствовать</td><td>Озлобленность Родионова против Добрармии, общая для всех правонастроенных казаков, огорчила меня донельзя: какое безумие подобная распря, раздор, политиканствующие дискуссии в столь тяжкий период, когда с красными далеко не окончено, когда опасности растут</td><td>1918</td></tr><tr><td>полотер</td><td>Вся прислуга: повара, судомойки, горничные, прачки, водопроводчики, полотеры, кучера, монтеры — в общем человек 200</td><td>1917</td></tr><tr><td>полузабытье</td><td>Долго лежал в полузабытье — разбитый и ослабевший</td><td>1936</td></tr><tr><td>полуинтеллигентный</td><td>Вот малоспособная и совсем еще не развитая девочка из полуинтеллигентной семьи</td><td>1914</td></tr><tr><td>полуодетый</td><td>При полном отсутствии кого-либо взрослого, ребятишки, полуодетые, босые или в непомерно больших тапочках, бродили и бегали по коридору</td><td>1940</td></tr><tr><td>полупочтенный</td><td>Вышли на сцену всё полупочтенные личности, которых мы с войны не видели только</td><td>1940</td></tr><tr><td>полусон</td><td>Днем отдыхал и в полусне увидел некий поворот дорожки в милом мне парке, и притом ранней весной, и как было больно на душе</td><td>1918</td></tr><tr><td>полутемно</td><td>В часовне полутемно и довольно душно</td><td>1927</td></tr><tr><td>полутемный</td><td>Теперь Собрание окончилось, все вошло в обычную колею, и ничто более не может нарушить однообразия длинных, ненастных, полутемных дней</td><td>1903</td></tr><tr><td>полушутя</td><td>Наблюдая эти странности, я полушутя пропел из «Кармен»: «Здесь, кажется, притон для всех контрабандистов»</td><td>1918</td></tr><tr><td>помертветь</td><td>Я помертвела от ужаса, но крепилась, боясь испугать и без того перепуганного мальчика</td><td>1935</td></tr><tr><td>поместительный</td><td>Каюта оказалась довольно поместительная, по 2 койке одна над одной, по обеим сторонам</td><td>1914</td></tr><tr><td>пообещаться</td><td>Пообещались ей принести завтра книгу</td><td>1907</td></tr><tr><td>поплестись</td><td>В час дня поплелись пешком обратно, и опять при отчаянной погоде</td><td>1917</td></tr><tr><td>пополудни</td><td>Митинг на Красной площади начался в пять часов пополудни</td><td>1936</td></tr><tr><td>поправление</td><td>Он тронул меня, справляясь о моем здоровье и выражая радость по поводу моего поправления</td><td>1902</td></tr><tr><td>пораженец</td><td>Так вот было время, когда стал вопрос, надо ли нам защищать родину или стоять против войны; тогда многим, стоящим за родину, было страшно встретиться с пораженцем; было ли наоборот, что пораженцу было нравственно страшно встретиться с патриотом</td><td>1934</td></tr><tr><td>пораздумать</td><td>Утром, проснувшись, Миша сказал, что, пораздумав во время бессонной ночи, пришел к выводу — ехать сейчас в Батум не надо</td><td>1939</td></tr><tr><td>поранение</td><td>При столкновениях с полицией многие из толпы получили серьёзные поранения</td><td>1910</td></tr><tr><td>порывание</td><td>Чувствуется порывание куда-то ввысь, к чему-то бесконечно хорошему и идеальному, но наряду с этим является и легкое недоверие к этой чистоте и идеальности</td><td>1915</td></tr><tr><td>порывисто</td><td>Еще пауза, и старший рабочий, на которого пал жребий, порывисто встает и исчезает в темноте</td><td>1904</td></tr><tr><td>поседлать</td><td>На всякий случай поседлали лошадей</td><td>1920</td></tr><tr><td>посейчас</td><td>Чуть не повальные обыски и бесконечные аресты, идущие и посейчас в Петербурге, заставили меня увезти ее в Кемере и хранить там, подальше от жандармских рук</td><td>1906</td></tr><tr><td>поскотина</td><td>К счастью, вода быстро пошла на убыль, мосту же грозила опасность: подмыло и верхняя часть моста повисла, опасно даже переходить, скот был на воле, все пошли встречать скот и прогонили с поскотины в Заришну</td><td>1923</td></tr><tr><td>посовеститься</td><td>На саботаж средств нет, а городские товарищи, заведовавшие забастовочными или стачечными капиталами, не посовестились растратить что-то громадную сумму, будто бы более 2-х млн</td><td>1918</td></tr><tr><td>потолковать</td><td>Вечером я подробнее потолкую с тобой об этом, а сейчас надо заканчивать, ибо завтрак уже готов</td><td>1936</td></tr><tr><td>поусердствовать</td><td>В сцене встречи Гвидона очень уж поусердствовали и одновременно с ударом барабана сделали выстрел</td><td>1908</td></tr><tr><td>поутру</td><td>Поутру часа два гулял с Гаршиным и Мендом, заходили на набережной в лавки, были на базаре</td><td>1913</td></tr><tr><td>похваливать</td><td>Ее похваливают, как некогда хвалили гладиаторов в цирке, и думают, что этого и довольно</td><td>1916</td></tr><tr><td>похлопотать</td><td>Я прошение его принял и обещал похлопотать</td><td>1907</td></tr><tr><td>починочный</td><td>Меня сменил Эрнст, когда-то он работал мальчиком в слесарной починочной мастерской</td><td>1937</td></tr><tr><td>почтительный</td><td>В перерыве советская комиссия выработала примирительную формулу, в которой сохранена «отмена», но вставлена в довольно почтительную рамку</td><td>1909</td></tr><tr><td>преблагополучный</td><td>Плаваем преблагополучно, благодаря необыкновенно высокой воде, тогда, как обычно в это время года, судоходства на Оке почти не бывает</td><td>1902</td></tr><tr><td>превесело</td><td>Время мы провели премило и превесело, чувствовали себя так развязно, непринужденно</td><td>1917</td></tr><tr><td>превкусно</td><td>Несмотря на бедность, едим с Кучерявым превкусно: устрицы, лангусты, всякие рыбки и персики со сливками</td><td>1918</td></tr><tr><td>превкусный</td><td>В Ржеве мне подали в вагоне превкусного жареного цыпленка</td><td>1910</td></tr><tr><td>прегадкий</td><td>Ждал Сергея Юрьевича, который по телефону мне объявил, что в прегадком настроении, ко мне вряд ли приедет, и просил заехать в театр, где он сидит один</td><td>1906</td></tr><tr><td>преглупый</td><td>Губа и щека раздулись, и лицо стало преглупое</td><td>1903</td></tr><tr><td>предвечерие</td><td>Звонким смехом и шутками они разбили голубую стеклянную тишину предвечерья</td><td>1929</td></tr><tr><td>предвечерний</td><td>В этот предвечерний час в больнице никого из врачей и медсестёр не оказалось</td><td>1940</td></tr><tr><td>предводить</td><td>Император Николай обратился с манифестом к своему народу: «Предводимый германцами турецкий флот осмелился вероломно напасть на наше Черноморское побережье</td><td>1914</td></tr><tr><td>председательствование</td><td>Он настаивает на том, чтоб я взял на себя председательствование новой «Зубовской комиссии» по выработке уложения Министерства искусства</td><td>1917</td></tr><tr><td>предубежденный</td><td>Не знаю только, отчего он так предубежден против инспектрисы г-жи Лихошерстовой, о которой он говорил мне как о женщине суровой, бессердечной и мстительной, которая будто бы старается пополнять Училище детьми низкого происхождения, чтобы более иметь в руках воспитанниц</td><td>1902</td></tr><tr><td>предуказывать</td><td>Получил сообщение о переговорах с немцами; если и не соответствует это истине, то все равно предуказует ее в будущем</td><td>1918</td></tr><tr><td>предупредительность</td><td>Не трудно также отметить обычную невозмутимую сдержанность китайцев, объясняющую тот удивительный факт, что при всей антипатии к иностранцам мы всюду встречаем предупредительность и вежливость</td><td>1907</td></tr><tr><td>предчувствоваться</td><td>Да, переворот предчувствовался, предсказывался, но формы его оказались никем не предусмотренными, неожиданными для всех</td><td>1917</td></tr><tr><td>предшествие</td><td>Служба, являющаяся лишь предшествием торжественного отпевания, сравнительно коротка для православного богослужения; все же она продолжается не менее часа</td><td>1915</td></tr><tr><td>презабавный</td><td>Приходил и маленький Георгий; он начинает презабавно говорить</td><td>1906</td></tr><tr><td>преинтересный</td><td>Потом пришел Бакст, читали дневники, Бакст преинтересно рассказывал об Испании, говорили о милом XVIII в</td><td>1906</td></tr><tr><td>прекурьезный</td><td>Это заседание было прекурьезным: среди чужой квартиры, среди египетских и греческих скульптур, среди икон и</td><td>1913</td></tr><tr><td>премиленький</td><td>Раньше всего они с большой гордостью показали мне их собачку, маленького и премиленького сеттера</td><td>1905</td></tr><tr><td>премило</td><td>Если подойти к нему — премило улыбается</td><td>1920</td></tr><tr><td>премилый</td><td>Савва и ко мне привез кипу детских писанных поздравительных речей; писали дети 8-13 лет, и премилые речи; я в ответ велел выбрать и послать им по христианской книге</td><td>1908</td></tr><tr><td>прение</td><td>Разом он был прочитан и без всяких прений сгоряча принят</td><td>1903</td></tr><tr><td>преотвратительный</td><td>Преотвратительная осенняя дождливая и слякотная погода при сильном ветре, пронизывающем буквально до костей</td><td>1914</td></tr><tr><td>препоганый</td><td>Приехали в город около полудня, заняли препоганое ветхое здание — деревянный флигель с выбитыми окнами, загрязненный до невероятности, масса валяющегося окровавленного белья</td><td>1914</td></tr><tr><td>препотешно</td><td>И один вернувшийся снова на позиции к нам мой казачонок препотешно рассказывал нам, как за ним ухаживали в одном таком лазарете</td><td>1914</td></tr><tr><td>препотешный</td><td>Этот маленький господинчик со своим горбатым клювом имеет препотешный вид в форме горца Дикой дивизии</td><td>1916</td></tr><tr><td>препроводительный</td><td>Он сказал, что там у него знакомые командир и офицеры и что с его препроводительным письмом меня, вероятно, очень хорошо там примут, тем более что большевистская деморализация совершенно не коснулась крейсера</td><td>1917</td></tr><tr><td>препровождать</td><td>Беднягу арестовали в Могилеве и препровождали в Петропавловскую крепость</td><td>1918</td></tr><tr><td>препровождаться</td><td>Сюда, как и живые преступники в общем административном порядке, препровождаются души из всех деревень этого уезда</td><td>1907</td></tr><tr><td>пререкание</td><td>После долгих пререканий 1-й взвод поручика Тургиева пошел, 2-й же взвод барона Фиркса отказался, требуя подробного разъяснения обстановки</td><td>1917</td></tr><tr><td>пресерьезно</td><td>На вопрос Глазунова, обращенный к Римскому-Корсакову, «чем думаете заниматься летом», последний пресерьезно ответил: «А что ж, буду писать попурри»</td><td>1908</td></tr><tr><td>пресимпатичный</td><td>Вчера заходил пресимпатичный и преумнейший Борис Пастернак</td><td>1940</td></tr><tr><td>прескверный</td><td>Разместились мы в Петрогоровке прескверно</td><td>1920</td></tr><tr><td>претолстый</td><td>Почти все старики и бабы толстые претолстые, важные такие</td><td>1919</td></tr><tr><td>преуморительный</td><td>Зрелище было преуморительное, но мы старались не смеяться, чтоб не помешать ему</td><td>1920</td></tr><tr><td>преуспеяние</td><td>Да поможет бог этому правительству повести Россию к славе и преуспеянию</td><td>1917</td></tr><tr><td>приволье</td><td>Поле, луг были для меня лишь местом приволья, где можно было иногда порезвиться</td><td>1921</td></tr><tr><td>привскакивать</td><td>Известие это заставило даже привскочить с радости — значит, прорвать блокаду не невозможно</td><td>1904</td></tr><tr><td>привходить</td><td>И тысячи случайностей, людей, привходящих в круг моей жизни</td><td>1920</td></tr><tr><td>пригонка</td><td>Утром во время зарядки аккумуляторов сгорел подшипник дизель-мотора; пришлось остаться в Куйвасте и заняться пригонкой запасного</td><td>1915</td></tr><tr><td>приготовительный</td><td>Ученик приготовительного класса, приготовишка, бывало, стоит у доски, учитель на кафедре в синем фраке с золотыми пуговицами приказывает: — Напиши мне мир через «и» восьмиричное</td><td>1935</td></tr><tr><td>приготовлять</td><td>Поехал домой и начал приготовлять квартиру к приему гостей</td><td>1940</td></tr><tr><td>приготовляться</td><td>Все приготовлялось к довольно бурному походу, ибо в море ожидалась значительная качка</td><td>1918</td></tr><tr><td>приискание</td><td>Жорж и я решили отправиться на берег первыми для размена денег и приискания помещения</td><td>1920</td></tr><tr><td>приискивать</td><td>Гавриленко, знающий все и всех, обещал приискать нам какую-нибудь квартиру, чтобы передохнуть</td><td>1919</td></tr><tr><td>приказание</td><td>Однажды, получив приказание взять какую-то деревню приступом, он повел полк, но был ранен в бок</td><td>1920</td></tr><tr><td>прилаживаться</td><td>Удивительно, как это до сих пор еще не могу приладиться сообразить в первый же момент об осечке и нажимаю, и нажимаю</td><td>1927</td></tr><tr><td>примиряться</td><td>Готов тогда примириться с целым рядом погрешностей во всех отношениях и хочется приветствовать режиссера и артистов за миг художественного наслаждения</td><td>1912</td></tr><tr><td>приналечь</td><td>Особенно должен приналечь я на латынь, алгебру, французский, историю и географию</td><td>1914</td></tr><tr><td>приниженность</td><td>Гордые, надменные степняки ничего и никого не боялись, наоборот, скорее внушали страх и приниженность у местных китайцев</td><td>1908</td></tr><tr><td>приниженный</td><td>чередной фортель, — приниженные мольбы о каком угодно мире с Германией — застал послов именно за обсуждением какого-то — осторожного, правда, — нового изъявления дружественности большевикам</td><td>1918</td></tr><tr><td>принужденный</td><td>И когда я живу с сознанием того, что вечером я запишу все, что я сделала за день, я невольно себя во многом сдерживаю, чтобы не быть принужденной вечером сознаться самой себе в своих дурных поступках или помышлениях</td><td>1905</td></tr><tr><td>приохочивать</td><td>Я нарочно стараюсь приохотить себя к науке — много занимаюсь, буду — еще больше</td><td>1922</td></tr><tr><td>приплестись</td><td>Приплелась к нам оставленная на произвол судьбы худая собака, и её мы угостили и отправились в обратный путь</td><td>1927</td></tr><tr><td>припомаживать</td><td>Припомаженные на прямой (английский сзади) и на концах подвитые, подкрашенные волосы на голове, завитые усы</td><td>1912</td></tr><tr><td>прискучивать</td><td>Я не очень разговорчив, иногда я могу только молчать, и, вероятно, очень скоро прискучил бы Вам</td><td>1940</td></tr><tr><td>присные</td><td>Не потому, что рвусь в бой как «верный подданный Николая», а просто потому, что в противоположность 1904 году, не имею оснований считать эту войну исключительно делом рук царя и его присных — она во многом народна</td><td>1914</td></tr><tr><td>приспособляться</td><td>Начали устраиваться и приспособляться к новым условиям жизни</td><td>1914</td></tr><tr><td>присутствование</td><td>В 2 часа я отправился в Мариинский театр для присутствования на генеральной репетиции 1-го акта балета барона Врангеля «Дочь Микадо»</td><td>1902</td></tr><tr><td>присылка</td><td>Написал автору благодарственное письмо за присылку книги</td><td>1926</td></tr><tr><td>прихварывать</td><td>Ей сделано несколько операций, она прихварывает и сейчас будто бы</td><td>1909</td></tr><tr><td>причитывать</td><td>Быть может она выплакивала, причитывая, свое личное горе, — так неподдельно было горе её</td><td>1914</td></tr><tr><td>приютиться</td><td>На другом берегу стоит стена леса у подножия которой приютились серые лачуги поселян</td><td>1929</td></tr><tr><td>проблематический</td><td>Привлекает их, по-видимому, весьма проблематическая возможность продолжать в Праге общее образование</td><td>1921</td></tr><tr><td>проблуждать</td><td>Несмотря на ушиб ноги, Ободин бросился в лес и проблуждал в нем до рассвета</td><td>1914</td></tr><tr><td>пробродить</td><td>Забрели далеко, она опоздала на поезд, и мы пробродили еще час по Павловским улицам, разглядывая в окнах лавок пасхальные угощения и подарки</td><td>1907</td></tr><tr><td>проваландаться</td><td>Я проваландался дома и ничего не сделал</td><td>1912</td></tr><tr><td>провиденциальный</td><td>Как во времена Аксаковых, Киреевского, Каткова, идея провиденциальной мировой миссии России возбуждает в эти дни умы москвичей</td><td>1914</td></tr><tr><td>провизия</td><td>Спустил бы я ее на воду, запасся бы провизией на полгода, поставил бы мною изобретенный перегонный куб и выехал бы я сам — друг с мохнатым псом — в океан-море на валах покачаться, к незнакомой стране пристать, с ветром поспорить</td><td>1921</td></tr><tr><td>провинциализм</td><td>Ничего себе, хотя трудно здесь уберечься от налета провинциализма: везде ведь здесь третий сорт</td><td>1919</td></tr><tr><td>проглянуть</td><td>К Пасхе, наверно, все высохнет, и, может быть, робко проглянет изумрудная травка</td><td>1923</td></tr><tr><td>продекламировать</td><td>Теперь уж никому и никогда на этих полях не удастся стать в позу Руслана и продекламировать: «О, поле, поле, кто тебя усеял мертвыми костями</td><td>1914</td></tr><tr><td>продовольствоваться</td><td>Худо то, что средства материальной части у меркуловцев крайне ограничены, а условия снабжения местными средствами очень затруднительны и сейчас же обостряют отношение населения к армии, вынужденной продовольствоваться за счет населения</td><td>1922</td></tr><tr><td>продувной</td><td>В конце концов, он решил, что я ужасный жулик, способный перещеголять самого продувного юриста</td><td>1912</td></tr><tr><td>прозодежда</td><td>Типично пролетарского вида женщина лет 40 в красном платке, в прозодежде, с корявыми от черной работы руками, сидит на скамье бульвара, углубившись в книгу</td><td>1934</td></tr><tr><td>проканителиться</td><td>Хотели, было, идти сейчас же, после обеда, но проканителились и вышли часов в 5 после чая</td><td>1918</td></tr><tr><td>прокламация</td><td>Думаю, что рассказы подобных пленных гораздо благотворнее и успокоительное действовали на население, чем самые мирные и торжественные прокламации завоевателей, подкрепленные экзекуциями</td><td>1900</td></tr><tr><td>проконспектировать</td><td>Прочла и проконспектировала 50 страниц хвостовского «Востока», еле-еле</td><td>1921</td></tr><tr><td>прокормление</td><td>А ведь ради прокормления семьи и просящих есть детей к большевистским ногами, склонятся очень многие непреклонные при других обстоятельствах шеи</td><td>1917</td></tr><tr><td>пролаза</td><td>Руководит вездесущий Эфрос — проныра, пролаз, деляга</td><td>1930</td></tr><tr><td>промер</td><td>Оказывается, он уже сделал промер глубины</td><td>1937</td></tr><tr><td>проникнутый</td><td>Это странное явление: их совесть, обыкновенно не очень щепетильная, так, однако, проникнута христианством, что на каждом шагу ставит перед ними вопросы возмездия и наказания</td><td>1915</td></tr><tr><td>проплутать</td><td>Боясь преследования, мы стали удирать болотами, потеряли направление и, проплутав часа два, пришли, как зайцы, на старое место (в страхе и люди бегут кругами)</td><td>1925</td></tr><tr><td>проредактировать</td><td>Всего за эти дни проредактировала и проклассифицировала 477 рефератов</td><td>1931</td></tr><tr><td>проситель</td><td>В приемной принял просителей, которые всюду меня отыскивают, как коршуны падаль</td><td>1907</td></tr><tr><td>простец</td><td>Поэтому я люблю заходить иногда к этому простецу, зная, что он не оскорбит меня неуместными допросами</td><td>1922</td></tr><tr><td>протанцевать</td><td>При нем протанцевали два раза всю репетицию по его же желанию</td><td>1900</td></tr><tr><td>протежировать</td><td>Махарину протежировала Павловская, обучала ее и находила, что у ней необыкновенные голосовые данные</td><td>1900</td></tr><tr><td>протекший</td><td>Вот и протекший день почти целиком ушел на это</td><td>1935</td></tr><tr><td>протелефонировать</td><td>Сегодня мне протелефонировали от Стейнвея, что билеты уже взяты на «Арабик», который отходит 1-го апреля, и французская виза уже проставлена на паспорте</td><td>1925</td></tr><tr><td>противоположение</td><td>Он всё время говорит по-французски, спрашивает читать курсисток, у них же выспрашивает значения тех или других слов посредством синонимов и противоположений, заставляет курсисток говорить по-французски эти коротенькие фразы, понимает их по-русски только при переводе — словом, заставляет работать</td><td>1914</td></tr><tr><td>противоправительственный</td><td>Он в Иркутске сказал речь противоправительственную</td><td>1911</td></tr><tr><td>противуположный</td><td>Пороги довольно быстрые; на противуположной стороне красивый и странно густой лес, но ничего особенно выдающегося</td><td>1905</td></tr><tr><td>протолкаться</td><td>В банк нельзя было протолкаться</td><td>1904</td></tr><tr><td>прохворать</td><td>Всю неделю прохворал — бессоница, лихорадит, нервы</td><td>1913</td></tr><tr><td>прочувствованный</td><td>Я читала «Победу Женщины» с особым интересом, потому что в ней звучит, много знакомых и прочувствованных тонов</td><td>1919</td></tr><tr><td>пустынность</td><td>Разваленность в сумерках не так заметна, только пустынность даёт себя знать</td><td>1929</td></tr><tr><td>пустяшный</td><td>Это не может быть пустяшным делом, раз тебя вызывает Уголовное Отделение</td><td>1933</td></tr><tr><td>пчельник</td><td>Между тем, пчельник, огород и картофель — это главная наша надежда, особенно если к осени не произойдет значительных перемен к лучшему</td><td>1919</td></tr><tr><td>пьянино</td><td>Перевезли пьянино, прекрасный резонанс, но играть не игралось</td><td>1905</td></tr><tr><td>равноправность</td><td>Царь не видит, не понимает того глубокого изменения, которое его законы о равноправности в вере внесли в нашу жизнь</td><td>1908</td></tr><tr><td>разбранить</td><td>Но Сережа пришел, какой-то сердитый, разбранил мои стихи и стал пить чай</td><td>1909</td></tr><tr><td>разбросанность</td><td>Впрочем, в столь меня смутившем инциденте с созывом Театральной комиссии едва ли действовала какая-либо интрига, а скорее, это произошло из-за разгильдяйства Макарова, который вообще щеголяет какой-то «артистической разбросанностью»</td><td>1917</td></tr><tr><td>разверстывать</td><td>Дела как будто продвинулись вперед я написал что всего разверстано 24 человека неграмотных и малограмотных</td><td>1930</td></tr><tr><td>развинченность</td><td>Несомненно, что на эту развинченность большое влияние имела история князя Волконского и его быстрый уход</td><td>1902</td></tr><tr><td>развинченный</td><td>Иногда харбинские улицы начинают напоминать нам то, что месяц тому назад мы видели в Александровском саду и на Кронверкском проспекте: те же малостесняющиеся парочки, та же развинченная походка, те же кудлы волос…</td><td>1918</td></tr><tr><td>разволновать</td><td>Это вконец разволновало меня — и я, конечно, не сомкнул глаз всю ночь, хотя и принял брому</td><td>1926</td></tr><tr><td>развратитель</td><td>И спекулянты они, и бабники, и развратители народной совести, и обдиратели бедноты</td><td>1921</td></tr><tr><td>развязность</td><td>В этом у меня приобретается привычка, я все менее стесняюсь и чувствую даже некоторую приятность с развязностью</td><td>1902</td></tr><tr><td>развязный</td><td>Удивительно, как этот человек после всего процесса его арестования и будущего суда был развязен, будто ничего не произошло с ним особенного</td><td>1912</td></tr><tr><td>разговляться</td><td>Где-то святили «пасхи», разговлялись, христосовались и сегодня христосуются</td><td>1937</td></tr><tr><td>разгорячаться</td><td>Потом я снова танцевала и так разгорячилась, что села у открытой форточки</td><td>1938</td></tr><tr><td>раздевальня</td><td>Вся публика присутствует раздетая, верхние платье снимают и сдают, на паперти устроена раздевальня и швейцары</td><td>1929</td></tr><tr><td>раздирающий</td><td>До нас доносились раздирающие крики избиваемых, сливавшиеся в один неистовый гул с диким победным ревом ихэтуанцев</td><td>1900</td></tr><tr><td>раздумчивый</td><td>Сегодня перед грозой не было душно, а чрезвычайно тихо и раздумчиво</td><td>1930</td></tr><tr><td>раздумываться</td><td>Мне не хотелось бы судить ее и говорить про нее дурно, но чувство тяжелой горечи невольно заставляло раздуматься над ее действиями и характером</td><td>1922</td></tr><tr><td>раззнакомливаться</td><td>Вера сказала, что, коли я не даю разъяснения своим небрежностям, мы должны раззнакомиться</td><td>1913</td></tr><tr><td>раззолачивать</td><td>Чудная погода, и мне пригрезились горизонты, раззолоченные деревья, и грустно стало</td><td>1917</td></tr><tr><td>разлезаться</td><td>Надо искать режиссеров и стараться дело поднять, ибо оно разлезается по швам</td><td>1906</td></tr><tr><td>разноречивый</td><td>Положение в течение всего дня было неопределенное; слухи по телефону сообщали самые разноречивые</td><td>1917</td></tr><tr><td>разнохарактерный</td><td>Дело в том, что перед началом действий, когда я вырабатывал решение, то встретился с трудностью, заключавшейся в том, что полоса предстоящих действий корпуса делится на два разнохарактерных участка</td><td>1939</td></tr><tr><td>разнузданность</td><td>Последний акт страшной русской трагедии, трагедии голода и разнузданности, вытекающей из русского невежества, политического и всякого другого</td><td>1921</td></tr><tr><td>разубирать</td><td>Через Кабань до лагеря добрались на пароходике «Джигит» красиво разубранном цветами, зеленью и флагами</td><td>1908</td></tr><tr><td>разуверять</td><td>Вернувшись домой, написал Лидии Ивановне милое письмо, утешая и разуверяя её в неуспехе</td><td>1912</td></tr><tr><td>разъясниваться</td><td>Погода переменная, но с 3-х разъяснилось</td><td>1925</td></tr><tr><td>рамолик</td><td>Всмотрелся я сегодня в коменданта крепости Кайгородова: типичный клинической формы рамолик, выражение лица слабоумное, при осаде крепости вся, вероятно, надежда будет на его жену — полную жизни энергичную даму типа русской боярыни</td><td>1915</td></tr><tr><td>рамольный</td><td>Как ни старались ставить в Императорском театре в угоду рамольным старикам и развращенной молодежи балеты, а все того нельзя, что на частной сцене</td><td>1903</td></tr><tr><td>раскассировать</td><td>И будь совет Московского университета во сто раз лучше теперешнего, раскассированного, все равно он был бы ничтожной пылинкой</td><td>1915</td></tr><tr><td>распивочный</td><td>Благотворители завели у нас в театре распивочную и навынос</td><td>1906</td></tr><tr><td>распорядительность</td><td>Только благодаря распорядительности и храбрости Российского удалось спасти батарею со сравнительно небольшими потерями</td><td>1904</td></tr><tr><td>распропагандировать</td><td>Нижние чины распропагандированы революцией, но, конечно, мудреных программ не понимают, а думают о бунте и грабеже</td><td>1916</td></tr><tr><td>распроститься</td><td>На вокзале распростился, пообедал, уселся в вагон, идущий на Полоцк, и начал засыпать</td><td>1916</td></tr><tr><td>распубликовывать</td><td>Из распубликованных материалов к съезду выдающееся значение будут иметь тезисы о социалистическом строительстве на следующее пятилетие</td><td>1934</td></tr><tr><td>распутье</td><td>Да я на распутьи, в ближайшие дни моя судьба должна решиться мною</td><td>1924</td></tr><tr><td>распыленность</td><td>Причин неудачи много: 1) распыленность в работе; 2) иногда неудачный подход; 3) недостаток средств</td><td>1928</td></tr><tr><td>расстреливание</td><td>Теперь наши моряки принуждены с берега наблюдать ужасную картину расстреливания своих судов</td><td>1904</td></tr><tr><td>рассудочность</td><td>Теперь нет прежней стихийной непосредственности, больше рассудочности, больше осознанного, пережитого; и самая тоска осложнилась чисто земным содержанием</td><td>1910</td></tr><tr><td>рассудочный</td><td>Читает тучи писем и телеграмм спокойно и рассудочно</td><td>1937</td></tr><tr><td>растравлять</td><td>День мой будет занят, а на домашнюю гнусь я благоразумно постараюсь закрыть глаза и не думать о ней и не растравлять того, что внутри, наболело так</td><td>1919</td></tr><tr><td>растратиться</td><td>Я и раздал, да и растратился, зайдя в бельевой магазин</td><td>1929</td></tr><tr><td>расхварываться</td><td>Мама и папа ушли к Эре, который совсем расхворался, у него жар</td><td>1901</td></tr><tr><td>расхлябываться</td><td>Это был тоже «безалаберный», распущенный месяц: отдыхал, не работал, распустился, расхлябался</td><td>1927</td></tr><tr><td>расцеловываться</td><td>На прощание я расцеловался с Маяковским</td><td>1928</td></tr><tr><td>расчесть</td><td>Сегодня — заслуженный профессор, а завтра не понравился кому-нибудь — возьмут и разочтут</td><td>1904</td></tr><tr><td>ребяческий</td><td>Я восторгался не менее Листова, отдававшегося радости свидания с природой с трогательной, ребяческой откровенностью</td><td>1922</td></tr><tr><td>ревизовать</td><td>Коковцев полагал, что подходящих ревизоров очень мало, а ведь ревизовать пришлось бы и Петербургский университет; Коковцев сверх того не знает, приносят ли вообще сенаторские ревизии пользу или вред</td><td>1911</td></tr><tr><td>редакторша</td><td>Как-то завелось, что я целую руку у редакторши Н</td><td>1916</td></tr><tr><td>режиссерство</td><td>Кажется, уже много всего было перепробовано, и Репертуарный совет, и очередное режиссерство, и распределение ролей, и выбор репертуара — нет — в них уже самих нет силы</td><td>1908</td></tr><tr><td>резнуть</td><td>Резкий звук резнул слух и мгновенным эхом отдался в поле</td><td>1900</td></tr><tr><td>речонка</td><td>Дело в том, что в нормально-погожее время Аркагала представляет из себя маленькую шуструю речонку, глубиной немного выше колен и шириной в полтора десятка метров</td><td>1936</td></tr><tr><td>роздых</td><td>Мы втроем выпили небольшой графинчик водки, но мне не стало веселее: тоска и боль за мужа не дают мне роздыха</td><td>1936</td></tr><tr><td>романический</td><td>Роман Кингслея «Hereward the Wake» — приятно уходить в далекое — в японскую историографию (статья Кюнера) приходил чуть ли не по 2 раза в неделю, часто даже надоедая нам своими излияниями, духовными и романическими</td><td>1935</td></tr><tr><td>ропот</td><td>Голодные, с разбитыми ногами, красноармейцы выполняют марши терпеливо, без единого намека на ропот</td><td>1920</td></tr><tr><td>ростепель</td><td>На улице ростепель, слякоть, скользина, дождь и снег</td><td>1939</td></tr><tr><td>ротозейство</td><td>Ведь ротозейство помогло контрреволюционерам поднять мятеж и захватить губернский город</td><td>1918</td></tr><tr><td>ругательски</td><td>Он ругает меня ругательски и требует, чтобы я помогал</td><td>1910</td></tr><tr><td>руготня</td><td>День отдыха — массовые экскурсии, много приехавших, пьяные, руготня</td><td>1936</td></tr><tr><td>руководиться</td><td>Масса причин, которыми руководятся мучители в применении своей силы над слабыми</td><td>1902</td></tr><tr><td>рутинер</td><td>И действительно: нелегко будет ему справляться с шайкой служащих, затверделых интриганов и рутинеров</td><td>1914</td></tr><tr><td>рутинёр</td><td>И действительно: нелегко будет ему справляться с шайкой служащих, затверделых интриганов и рутинеров</td><td>1914</td></tr><tr><td>с несомненностью</td><td>Вижу, что страна, с несомненностью, неуклонно прогрессирует, в особенности в области промышленной жизни</td><td>1935</td></tr><tr><td>садический</td><td>Его жадность к крови, к смерти, к убийствам, ко всему страшному, его почти садическая страсть к страданиям — ограничила его материал</td><td>1931</td></tr><tr><td>самодовлеющий</td><td>Что же после этого удивительного, если и дети учатся для отметок, понимаемых как нечто самодовлеющее, каким бы путем они не были приобретены</td><td>1912</td></tr><tr><td>самозабвение</td><td>Варвара Николаевна наоборот — порой очень невыдержанна; мы дважды ругались с ней до самозабвения, но сейчас же мирились</td><td>1916</td></tr><tr><td>самолюбивый</td><td>Он очень самолюбивый человек, любит доминировать, а за последнее время он у царя никакой роли не играет, влияния у него там никакого, потому и сохнет</td><td>1901</td></tr><tr><td>самомнительный</td><td>Он очень прост и свободен, и ни тени, ни намека на грубость, самомнительную назойливость, на что-либо такое, что как-нибудь можно бы задеть, покоробить</td><td>1922</td></tr><tr><td>самоотвержение</td><td>Благодарю вас и всех чинов, честно исполнявших свой долг в бою, за самоотвержение, с которым послужили России и мне</td><td>1905</td></tr><tr><td>самоуслаждение</td><td>Писана пьеса, видимо, не без самоуслаждения, но языком хорошим, литературным, и не без знания сцены</td><td>1910</td></tr><tr><td>самохвальство</td><td>Дурных наклонностей у него нет, кроме некоторой наклонности к самохвальству, за что, собственно, он и не пользуется уважением</td><td>1905</td></tr><tr><td>сантиментальный</td><td>А веселье — неловко, и остается невольно фальшивая, сантиментальная болтовня</td><td>1906</td></tr><tr><td>сбираться</td><td>Соколова, которая сбирается тоже в Москву, чтобы посылать пищу заключенным, но не пришла</td><td>1921</td></tr><tr><td>свидеться</td><td>Я был сильно поглощён стремлением свидеться с друзьями</td><td>1918</td></tr><tr><td>святотатственный</td><td>Такие святотатственные мысли мелькали в моей голове</td><td>1917</td></tr><tr><td>сговариваться</td><td>За кулисами я сидел со Штейманом; мы сговаривались с ним посмотреть вместе на Лондон и окрестности, благо ни он, ни я их не видали</td><td>1913</td></tr><tr><td>сговорить</td><td>Наконец мы возвращаемся на пароход с рассыпающимся в песок стариком лодочником, взявшим с нас вчетверо против сговоренной платы</td><td>1913</td></tr><tr><td>сделаться</td><td>Вдруг что-то сделалось с глазами: пошли темные пятна, плохо вижу шрифт</td><td>1919</td></tr><tr><td>секретарство</td><td>Моя жена, кроме секретарства у консула, состоит заведующим иностранным отделом в бюро печати</td><td>1918</td></tr><tr><td>семитический</td><td>Это — небольшого роста, седенький армянин, с явно семитическим строением черепа, очень сдержанный, умеющих владеть собой, умный, хитрый и энергичный</td><td>1922</td></tr><tr><td>сжатость</td><td>Из-за сжатости всего повествования и простоты фраз</td><td>1939</td></tr><tr><td>сжиться</td><td>Он сжился с ними, полюбил их, но и не закрывает глаза на их недостатки, болея ими и стараясь об их искоренении</td><td>1908</td></tr><tr><td>синема</td><td>Идет интересная картина в синема</td><td>1934</td></tr><tr><td>скверно</td><td>Мы пели в большом зале, я — очень скверно, к тому же было раздражающее эхо в зале</td><td>1922</td></tr><tr><td>скверность</td><td>Матерная брань сознается русским человеком как величайшая скверность, и при возрождении простой человек, прежде всего, берется искоренять в себе эту дурную привычку, на это иногда употребляют годы</td><td>1926</td></tr><tr><td>скверный</td><td>Более 2-х недель льет дождь, погода скверная</td><td>1911</td></tr><tr><td>сконфуженно</td><td>Захава сконфуженно бормотал мне по телефону, что экземпляра с разрешением не нашли</td><td>1937</td></tr><tr><td>сконфуженный</td><td>Усталые и сконфуженные охотники, все в снегу, только что вернулись</td><td>1936</td></tr><tr><td>скопляться</td><td>Горе, скопляясь в одной душе все больше и больше, может в один прекрасный день загореться, вспыхнуть сразу, как сено, и все сгореть в огромном пожаре радости</td><td>1938</td></tr><tr><td>скоропреходящий</td><td>Правда, случаются и здесь неприятности, но они скоропреходящи и разрешаются благополучно, не оставляя дурных следов на наших отношениях</td><td>1913</td></tr><tr><td>скрывание</td><td>Какая сеть обмана, скрываний от меня</td><td>1910</td></tr><tr><td>скучища</td><td>К счастью, вся эта суетня и скучища подготовки к переезду, переезд и устройство в новом жилище теперь позади, и вспоминать обо всем этом не стоит</td><td>1940</td></tr><tr><td>слабосильный</td><td>Впечатление такое: какой-то слабосильный человек, нервный, не выдержал и задушил</td><td>1904</td></tr><tr><td>славолюбие</td><td>Отметил безмерное славолюбие Петрова и муки его от сознания падающей популярности</td><td>1909</td></tr><tr><td>слагаться</td><td>Личность всякаго человѣка слагается изъ двухъ его свойствъ: первое — умственныхъ способностей — памяти, сообразительности, способности вниманія, сосредоточенности (главное, отъ этой способности сосредоточенности вниманія), и второе — его способности живости сознанія возможности достиженія идеала совершенства</td><td>1908</td></tr><tr><td>сладостно</td><td>Ах, до чего сладостно бывало с ним (или с Серовым) разглядывать какие-либо прекрасные вещи — книги, картины</td><td>1918</td></tr><tr><td>сладострастник</td><td>А они в эту же ночь, вероятно, не раз продавали себя более смелым и откровенным сладострастникам</td><td>1917</td></tr><tr><td>сласти</td><td>Посмотрели еще и елку, которую Володя с Наташей хотели украсить сластями и зажечь вечером на мои именины</td><td>1916</td></tr><tr><td>сличение</td><td>Равным образом люблю я читать комментарии всяких творений какого бы ни было рода, истолкование их строка за строкой, сличение их с другими творениями и историческими и иными данными, и извлечение из всего этого выводов, теорий, воззрений</td><td>1918</td></tr><tr><td>словоизвержение</td><td>Она, как всегда, начала нести разную околесину — пришлось просить остановить это словоизвержение, ибо не имею времени и собираюсь в театр</td><td>1909</td></tr><tr><td>словоохотливый</td><td>Смотрит за дворцом словоохотливый запасный вахмистр конно-гвардеец Киселев</td><td>1908</td></tr><tr><td>словопрение</td><td>Суждено ли ему явиться моментом историческим, или это будет снова одно словопрение</td><td>1917</td></tr><tr><td>сломка</td><td>Проезжал 2 раза мимо Ильинских ворот, их уже не существует! Слышал, что сломка Сухаревой башни в нынешнем году не будет произведена</td><td>1933</td></tr><tr><td>смерзнуть</td><td>Вчера и сегодня льет дождь, и так холодно, что все смерзли, всего 11 градусов тепла</td><td>1918</td></tr><tr><td>сметка</td><td>Корнилов — сын казака и язычницы, совершенно необразованный, но хитрый и с военной сметкой</td><td>1917</td></tr><tr><td>смолкать</td><td>Коростель крикнул и смолк, первый коростель, которого я слышу этой весной</td><td>1908</td></tr><tr><td>снестись</td><td>Директору же департамента я посоветовал самому снестись с Мосоловым</td><td>1911</td></tr><tr><td>сношение</td><td>Нас обвинят в «керенщине», с которой мы боремся всеми силами, сношениях с левыми партиями, которых нет и в помине</td><td>1921</td></tr><tr><td>со всеми онёрами</td><td>Стол в штабе обжирательный; сегодня ели блины со всеми онерами и атрибутами</td><td>1916</td></tr><tr><td>совеститься</td><td>Раньше человек, делая частное свое дело, совестился этим, и небогатый отмаливал грехи в церкви, богач жертвовал на строительство нового храма</td><td>1939</td></tr><tr><td>согласительный</td><td>Создалось безвыходное положение, но решили не разъезжаться до тех пор, пока партии не придут к какому-нибудь согласительному результату</td><td>1917</td></tr><tr><td>содержатель</td><td>Каждый содержатель своего заведения или зазывала буквально хватали за руки и тащили к себе</td><td>1938</td></tr><tr><td>содержательница</td><td>Содержательница гостиницы «Кист», где живет Корин, немка</td><td>1916</td></tr><tr><td>созвать</td><td>После этого состоялось комсомольское собрание, созванное Фрейдовичем</td><td>1939</td></tr><tr><td>соизволение</td><td>Ему уже стало известно от старшего полковника о Высочайшем соизволении на отчисление Гаврилушки в Свиту</td><td>1913</td></tr><tr><td>солдатчина</td><td>Отголосок старого варварства — завоеванный город отдавался солдатчине на три дня</td><td>1918</td></tr><tr><td>сообразовывать</td><td>В настоящее время все распоряжения приходится сообразовывать с настроением больной России</td><td>1905</td></tr><tr><td>сорганизовывать</td><td>Здесь он как недурной организатор и вообще способный парень быстро сорганизовал группу</td><td>1930</td></tr><tr><td>сорганизовываться</td><td>Говорил о том, что нам нужно сорганизоваться и устроить собственные войска и крепко стоять за свою власть</td><td>1919</td></tr><tr><td>сослужение</td><td>Я прямо заявил, в течение разговора и на его вопросы, что как священника в сослужение его не приму, так как Православная Церковь не признает и не может признать их священства, главное потому, что они сами не признают рукоположение таинством</td><td>1900</td></tr><tr><td>соученица</td><td>Перевспоминали всех соучениц, курсисток многих, петроградцев-санитаров; поговорили о собственных приключениях и разных происшествиях, о письмах, которые получаются, и которые пишутся или будут написаны; о студенческом журнале и т</td><td>1917</td></tr><tr><td>сочувственно</td><td>Великий князь сказал, что желателен классический греческий стиль, на что государь отозвался сочувственно</td><td>1900</td></tr><tr><td>спанье</td><td>Это для него необходимая вещь, как еда и спанье</td><td>1933</td></tr><tr><td>спасительность</td><td>Буржуазия окончательно прониклась сознанием их необходимости и спасительности</td><td>1935</td></tr><tr><td>спаянность</td><td>Спаянность и отвага коллектива испугали врага</td><td>1937</td></tr><tr><td>спешность</td><td>При спешности и внезапности выезда моего из Вилейки не удалось приобрести порядочной толики сушеных белых грибов, которые мне обещал достать городовой врач</td><td>1915</td></tr><tr><td>спешный</td><td>Занимался массой спешных дел перед отъездом</td><td>1903</td></tr><tr><td>спортсменский</td><td>Между прочим обогнали «плавучий дом отдыха», остановившийся у пристани: пассажиры высыпали на берег и занялись физкультурою и купаньем, большинство — в легких спортсменских костюмах, не исключая и женщин</td><td>1925</td></tr><tr><td>средактировать</td><td>Юристы Правления должны средактировать договор</td><td>1932</td></tr><tr><td>срепетировать</td><td>Опера возобновлена и срепетирована хорошо</td><td>1913</td></tr><tr><td>сряду</td><td>Длились они несколько дней сряду</td><td>1901</td></tr><tr><td>старосветский</td><td>Какой-нибудь тип старосветского помещика, в мирную жизнь которого вот уже несколько лет врывается что-то, чего не знал, не хотел, что-то неумолимое, роковое, разрушившее весь его покой, весь строй его жизни</td><td>1904</td></tr><tr><td>столковываться</td><td>Будто бы на Западном фронте солдаты арестовали делегацию, ехавшую к французам столковываться о перемирии, и вступили в непосредственные переговоры с французскими солдатами</td><td>1918</td></tr><tr><td>сторожевка</td><td>Стыжусь признаться, что в первую минуту я обрадовался избавлению от необходимости идти в ночной поход, не будучи долее в силах переносить ночные сторожевки в открытом поле и постоянную готовность сесть на коня и идти, куда прикажут, во всякое время и погоду</td><td>1905</td></tr><tr><td>сторожиха</td><td>Теперь сторожей сменили женщинами-сторожихами, которые привели кладбище в лучший вид, и я отыскал могилу</td><td>1931</td></tr><tr><td>стосковаться</td><td>Но оба они сильно стосковались по дому</td><td>1925</td></tr><tr><td>странничество</td><td>Каждый переезд есть символ нашего странничества в этом мире и напоминает о том последнем совлечении, последнем путешествии, которое нам всем предстоит</td><td>1924</td></tr><tr><td>странствование</td><td>Мы смеялись над этим, сопоставляли русское странствование по диким лесам</td><td>1924</td></tr><tr><td>студиец</td><td>Сегодня Юля дала мне прочесть в «Правде» (дома мы выписываем только «Известия») статью о том, как занимается Станиславский со своими питомцами-студийцами</td><td>1937</td></tr><tr><td>стушевываться</td><td>Ставлен был на баллотировку вопрос, правильно ли поступило правление, но я горячо запротестовал против такой постановки дела, и он стушевался</td><td>1916</td></tr><tr><td>судоговорение</td><td>Митя говорил много, дельно и хорошо, держа себя как защитник в судоговорении</td><td>1907</td></tr><tr><td>суетня</td><td>Будет суетня, крики, охи, ахи, пыль, много потерянного времени и много скуки</td><td>1940</td></tr><tr><td>сулиться</td><td>Сегодня — день советской пропаганды; товарищи сулятся звонить в Москве во все колокола и издавать все звуки, какие они в состоянии издать</td><td>1919</td></tr><tr><td>сумрачно</td><td>В саду стоял густой запах тополей и берёз, сумрачно переплелись ветви над головой</td><td>1910</td></tr><tr><td>сутолока</td><td>Прогулка эта доставила мне большое удовольствие и очень освежила после консерваторской сутолоки</td><td>1912</td></tr><tr><td>сушь</td><td>Погода без перемен, жара, сушь, пыль</td><td>1935</td></tr><tr><td>сходка</td><td>На собрании решили устроить снова сходку на другой день, в воскресенье, в полчаса пятого, но при этом соблюдать крайнюю осторожность</td><td>1917</td></tr><tr><td>считка</td><td>Вечером у детей в присутствии Ермолинского шла считка пьесы «Светский случай», которую они собираются играть</td><td>1908</td></tr><tr><td>сызнова</td><td>Кажется, если все началось бы сызнова, у меня не хватит сил перенесть</td><td>1910</td></tr><tr><td>таксомотор</td><td>В три часа долго добивался таксомотора: куда ни позвонишь — все разобраны</td><td>1914</td></tr><tr><td>творило</td><td>Однако он, наконец, сделал решительный шаг, вошел в чуланчик, открыл погреб, стал на самый край черного квадрата, обозначавшего поднятое творило, и взял пистолет в правую руку</td><td>1864</td></tr><tr><td>тезоименитство</td><td>Встреча праздников и дней тезоименитств сопровождалось уборкой покоя, и тем, что чай пили в большой столовой</td><td>1920</td></tr><tr><td>телефонировать</td><td>Василий телефонировал из Порхова, что могут нам дать только пять солдат-мальчишек</td><td>1918</td></tr><tr><td>темь</td><td>В такие дни кругом стоит непроглядная темь, и лишь вблизи едва чернеют знакомые предметы</td><td>1937</td></tr><tr><td>теперешний</td><td>Недавно мы разговорились, талантлива ли теперешняя эмиграция</td><td>1934</td></tr><tr><td>типик</td><td>Интересный типик, которых мало встречаешь в жизни</td><td>1900</td></tr><tr><td>толки</td><td>Говорит, что много толков вызывает заключение контракта на 6000 руб</td><td>1910</td></tr><tr><td>толкотня</td><td>Толкотня и шум и в конце концов угрызения совести за невинно убиенное время</td><td>1916</td></tr><tr><td>томительно</td><td>Томительно тянулись дни в зимней снежной берлоге (разве можно сравнить ее с нашей замечательной хижиной)</td><td>1937</td></tr><tr><td>томительный</td><td>Томительное чувство безотложной необходимости разобраться в своих хранилищах и попутно отсутствие сил для этого</td><td>1936</td></tr><tr><td>торжественность</td><td>Маму называл он не иначе, как «сударыня», с какой-то неизъяснимой торжественностью умел сидеть на козлах, вытянув руки вперед, и, подобно Параше, не мыслил своей жизни вне нашего дома</td><td>1935</td></tr><tr><td>тотчас</td><td>Часа в четыре утра я проснулся, быстро накинул на себя тужурку и тотчас поднялся наверх</td><td>1906</td></tr><tr><td>треволнение</td><td>Роскошная картина деревенской или горной идиллии, вдали от городского шума и треволнений цивилизованной жизни</td><td>1905</td></tr><tr><td>трезвон</td><td>В городе шел целый день колокольный трезвон — празднование избавления от большевиков</td><td>1919</td></tr><tr><td>трепетаться</td><td>Только на верху осин трепещутся еще последние листочки, внизу же они стали стеной по-зимнему серые, и на сером бледные листья ореха: каждый орешник теперь остался с отборными листьями</td><td>1937</td></tr><tr><td>трескотня</td><td>Бомбардировка его окончилась только около 2 часов дня, ружейная же трескотня продолжалась до самой ночи</td><td>1904</td></tr><tr><td>трунить</td><td>Он наивно, добродушно улыбается и трунит над такой своей неожиданной метаморфозой</td><td>1914</td></tr><tr><td>тягостно</td><td>С утра было тягостно, чувствовалось, переменяется погода</td><td>1932</td></tr><tr><td>тягостный</td><td>Страшно тягостна эта неизвестность, мы сидим точно кроты в норке, но что делать, так, может быть, целее будем</td><td>1918</td></tr><tr><td>убежденно</td><td>Он ответил убежденно и с жаром: «Никто из поэтов не мог написать такую дрянь»</td><td>1923</td></tr><tr><td>уборная</td><td>После II акта и по окончании оперы я заходил к ней в уборную: первый раз с Николаем Андреевичем, а второй — с ним и с Софьей Николаевной</td><td>1900</td></tr><tr><td>увеселять</td><td>Всех увеселял довольно нахальный господин Стефан Стефанович Мокульский, который держал себя чересчур развязно с Наталией Владимировной, несмотря на присутствие Юрика Султанова</td><td>1926</td></tr><tr><td>увещание</td><td>Все мои попытки и увещаний, и добродушных шуток и, наконец, серьезных замечаний ни к чему не привели</td><td>1931</td></tr><tr><td>уговаривание</td><td>Новый корпусный комиссар Антонов вернулся с своего первого дебюта по уговариванию полков 120 дивизии идти на занятие назначенных им боевых участков; вернулся совсем растерянный и обескураженный, так как в Даниловском полку ему не дали говорить и заявили, что на позицию не пойдут, а когда он попытался взлезть на комиссарские ходули и пригрозить, то только быстрота шофера, успевшего выскочить из толпы, спасла товарища комиссара от «народного помятия ему боков»</td><td>1917</td></tr><tr><td>уговориться</td><td>Мы четверо уговорились, что поедем в кино отдельно</td><td>1939</td></tr><tr><td>удовлетворительно</td><td>В общем спектакль прошел удовлетворительно, но скучновато</td><td>1913</td></tr><tr><td>удовольствоваться</td><td>Она удовольствуется хозяйством, собаками, лошадьми</td><td>1912</td></tr><tr><td>удручение</td><td>И вот горе мое переживалось со многими оттенками: то одиночество, то отчаяние, то угнетение, то удручение то обида, то ревность</td><td>1926</td></tr><tr><td>удрученный</td><td>Настроение у него удрученное: говорит о смерти, о самоубийстве</td><td>1932</td></tr><tr><td>улажение</td><td>Готов ждать 48 часов для улажения конфликта, а потом французам останется только уйти</td><td>1919</td></tr><tr><td>улаживаться</td><td>Тяжело приходится с хозяйством, но что ж делать, нужно же кому-нибудь вести его, тетя на себя не берет, а ведь я теперь самая старшая, ну да, Бог даст, все уладится и к лучшему</td><td>1920</td></tr><tr><td>умиленно</td><td>Смотрит на него Репин, как кошка на сало умиленно, влюбленно</td><td>1914</td></tr><tr><td>умиленность</td><td>Служба его прекрасна: красота, строгость, благоговейность, — а теперь еще и умиленность и умиренность</td><td>1919</td></tr><tr><td>умирять</td><td>Исповедайтесь у других священников; быть может, благодать Божия размягчит и умирит души тех, кто прибегнет к этому таинству</td><td>1902</td></tr><tr><td>умывальная</td><td>Ванны нет, общая уборная и умывальная</td><td>1939</td></tr><tr><td>упоен</td><td>Южин теперь упоен сборами и думает, что хорошие сборы означают интерес публики к театру, но дело в том, что публика ходит совершенно неизвестная</td><td>1916</td></tr><tr><td>упрашивание</td><td>Почти все время провел в Музее в хождении и упрашивании Протасова, Корша</td><td>1928</td></tr><tr><td>урезка</td><td>Через некоторое время он был целиком, без поправок и урезок напечатан в журнале</td><td>1939</td></tr><tr><td>уславливаться</td><td>Василий телефонировал из Порхова, что могут нам дать только пять солдат-мальчишек</td><td>1918</td></tr><tr><td>услаждаться</td><td>Но жалко старика, так недолго услаждавшегося своим благополучием</td><td>1913</td></tr><tr><td>услать</td><td>Вечером Осипа услали в Рассоху, чтобы отнести часть вещей и принести масла</td><td>1935</td></tr><tr><td>условиться</td><td>Пообедал я в 4 часа, после обеда зашла к нам Винкерт, и в разговоре папа условился, чтобы она нам рекомендовала пианино, и она обещала завтра дать ответ</td><td>1921</td></tr><tr><td>условливать</td><td>В 5 часов пошли к Олсуфьевым (так было условлено еще на первый день Рождества)</td><td>1919</td></tr><tr><td>услужать</td><td>И утренний завтрак хорош: кофе с горячим молоком, услужающий льет одновременно в стакан кофе и молоко, хрустящие свежие булочки, швейцарское масло, ароматное и ярко-желтое</td><td>1935</td></tr><tr><td>услыхать</td><td>Однажды, услыхав это из окна, я вышел на улицу и присоединился к матросам, чтобы понять природу этих людей</td><td>1926</td></tr><tr><td>усумниться</td><td>Идя из академии, усумнился, но закон любви силен в мире, и я ею помогу ей</td><td>1922</td></tr><tr><td>усыпительный</td><td>Веселовский усыпительно прочел по печатному тексту свою заключительную главу</td><td>1916</td></tr><tr><td>утерпеть</td><td>Там заказали себе пельменей и мороженного причем Диана и здесь не утерпела чтобы не выпить бутылку фруктовой</td><td>1939</td></tr><tr><td>утеснение</td><td>Свою корреспонденцию он заканчивал указанием на дальнейшие утеснения свободного денежного оборота, в форме ограничения выдач из сберегательных касс</td><td>1930</td></tr><tr><td>утеснять</td><td>Дом, где все утеснены пространственно, перегружены работой (кроме экзотической Шуры), все более или менее больны чем-нибудь и все сохранили нравственную энергию и образ и подобие Божие</td><td>1935</td></tr><tr><td>утешительный</td><td>Восстание в северных округах области — не утешительная выдумка, а действительный факт</td><td>1919</td></tr><tr><td>утишать</td><td>Мне надо было чем-нибудь заполнить эту пустоту, надо было как-нибудь утишить свою ужасную душевную боль, ибо иначе я неминуемо пришла бы к тому, к чему пришел Шура</td><td>1916</td></tr><tr><td>утомительность</td><td>Мастер плодить речь до крайней утомительности</td><td>1905</td></tr><tr><td>утомленность</td><td>Вчера что-то устал здорово, чувствуется общая утомленность</td><td>1936</td></tr><tr><td>утомленный</td><td>Я так ужасно страдала и была утомлена, что мечтала только об одном — о покое</td><td>1921</td></tr><tr><td>утрировка</td><td>Труппа эта бесспорно делает большие успехи, и, если бы исключить известную суету и утрировку, — исполнение образцово</td><td>1904</td></tr><tr><td>ухаживатель</td><td>Она сидит в партере, как у себя дома, без перчаток, около нее какой-то ухаживатель, потом в антрактах она в коридоре около моей ложи так громко хохотала и разговаривала с молодежью, что мне пришлось выйти из ложи, чтобы убедиться, что это за шум</td><td>1913</td></tr><tr><td>ухарский</td><td>Придя курить в наш клуб, уборную, они расселись по окнам, приняли ухарский вид, заломили фуражки на затылки, словно им теперь сам черт не брат</td><td>1916</td></tr><tr><td>ухарство</td><td>Приписывают нашему тихому, молчаливому, но и железному солдату, шинели которого они сами не стоят, или какое-то бессмысленное ухарство и презрение к врагу и к смерти, или же наивную жалость и ухаживанье за раненым врагом и братство с ним «на поле брани в тьме жуткой ночи»</td><td>1914</td></tr><tr><td>ухватка</td><td>Тщательно избегали говорить о войне, зато перебрали всех наших меценатов, их обычаи и ухватки</td><td>1917</td></tr><tr><td>ухлопать</td><td>В январе было много кутежей и понапрасну ухлопано денег</td><td>1911</td></tr><tr><td>учительствовать</td><td>Пять месяцев назад он мирно учительствовал в астурийской деревушке, войнами занимался только по учебнику истории</td><td>1936</td></tr><tr><td>уютность</td><td>При внешней неуютности, такая надежная, такая теплая внутренняя уютность</td><td>1939</td></tr><tr><td>уясняться</td><td>Однако уже в 1918 году масштабы событий начинают мало- помалу уясняться моим сознанием</td><td>1935</td></tr><tr><td>фазис</td><td>Китайская литература есть литература тысячелетий, отразившая жизнь человека во всех ее фазисах</td><td>1907</td></tr><tr><td>фанфаронство</td><td>Это молодой человек очень прямой и честный, получивший образование в Лицее, сохраняющий внешний лоск, но не зараженный фанфаронством и самомнением</td><td>1915</td></tr><tr><td>фатоватый</td><td>Прежде попадались офицеры нахальные, попадались наглые, фатоватые, но теперь — это что-то ужасное: грызут орехи, яблоки, пересмеиваются, как уличные мальчишки</td><td>1917</td></tr><tr><td>фейерверочный</td><td>Они играют и будут играть до конца свою каторжную игру со смелостью и упорством каторжников, которым все равно нечего терять, и которые всегда готовы к возможности так же фейерверочно улететь в ту грязь, из которой они вылезли</td><td>1917</td></tr><tr><td>фиглярничать</td><td>И все же революционная демократия в лице своих вождей продолжает глупо кривляться, пошло фиглярничать, принимать величественные позы</td><td>1917</td></tr><tr><td>физкультурница</td><td>Дипломаты разглядывали не без удовольствия наших очень хорошеньких, здоровых и жизнерадостных физкультурниц</td><td>1934</td></tr><tr><td>филистер</td><td>Никогда я не буду филистером, никогда не буду мещанином</td><td>1915</td></tr><tr><td>фойэ</td><td>Столы для банкета заняли все фойэ и гардероб бельэтажа</td><td>1928</td></tr><tr><td>формалистика</td><td>Враг всякой рутины, он стремился оживить всю гимназию, ратовал за новые приемы обучения; заботился о поднятии общего развития учениц и энергично боролся против той формалистики, которая уже свила себе гнездо в этой гимназии</td><td>1911</td></tr><tr><td>форменный</td><td>Голод, не иносказательный, не недоедание, а форменный голод — на пороге</td><td>1919</td></tr><tr><td>фразерство</td><td>Я ненавижу их фразерство, их позерство, их жестикуляцию, их патетику</td><td>1933</td></tr><tr><td>фразёр</td><td>Я положительно убедился, что Ковальский — фразер, противный, ломака и кривляка</td><td>1907</td></tr><tr><td>франтоватый</td><td>Приходит какой-то франтоватый студент и начинает есть, есть, есть… Мама говорит, что просто удивлялась, как можно съесть такую массу</td><td>1908</td></tr><tr><td>фрондировать</td><td>Он-де никогда политикой не занимался, всегда фрондировал, он-де — историк, ученый</td><td>1918</td></tr><tr><td>фуксом</td><td>Там чепуха, свиданий никому не дают, потом одним фуксом дали, потом опять всех высадили</td><td>1917</td></tr><tr><td>хвастливость</td><td>С этою хвастливостью связана беспечность, а за нее вот японцы и поколотили нас</td><td>1904</td></tr><tr><td>хлопотать</td><td>Я хлопотал о том, чтобы загородные дворцы-музеи не были бы рассматриваемы как один цельный организм, а входили каждый порознь своими представителями в общий Верховный совет по делам художественно-культурных музеев</td><td>1923</td></tr><tr><td>хлопотун</td><td>Сапега большой и, видно, ловкий хлопотун для своей нации</td><td>1921</td></tr><tr><td>хлыщеватый</td><td>Кожаное пальто с меховым воротником (кожа — коричневая), заграничные желтые туфли, заграничный костюм; подстрижен гладко, чисто выбрит, упитан, маленькие черные усики: богатенький и сытенький буржуа, хлыщеват</td><td>1931</td></tr><tr><td>ходкий</td><td>Самый ходкий вид шаржа — дружеская пародия, то есть откровенный подхалимаж</td><td>1931</td></tr><tr><td>ходко</td><td>Дул хороший ветер с моря, шлюпки ходко шли под парусами</td><td>1908</td></tr><tr><td>ходульно</td><td>Ни одной умной статьи, ни одного умного слова, все плоско и ходульно, все на заказ</td><td>1910</td></tr><tr><td>хозяйничание</td><td>К сожалению, о хозяйничании футуристов в Кремле он ничего сообщить не может</td><td>1917</td></tr><tr><td>художественность</td><td>Среди всех поэтов Англии Китс обнаружил больше всего художественности</td><td>1931</td></tr><tr><td>худосочие</td><td>Ясно — бывший костел, а теперь Бог знает что, на фронтоне васнецовская Богоматерь, а внутри униатское худосочие и бледность</td><td>1915</td></tr><tr><td>цветистость</td><td>Скучна и утомительна цветистость и бесконечный пафос речи героинь и героев и простых людей у обоих авторов</td><td>1912</td></tr><tr><td>церемонно</td><td>Мы церемонно поздоровались и несколько натянуто обменивались за обедом незначительными фразами на общие темы, потом так же холодно простились</td><td>1913</td></tr><tr><td>цинический</td><td>Грубость циническая ослабляет впечатление художественное, а не усиливает</td><td>1909</td></tr><tr><td>цукать</td><td>Жак женился, жена молодая (ну, она его уже цукает, скоро согнет в бараний рог)</td><td>1927</td></tr><tr><td>чванный</td><td>Я как-то ко всему отнеслась равнодушно, а вернулась в свой родной Питер, чванный и холодный, меня опять потянуло куда-нибудь поехать</td><td>1908</td></tr><tr><td>чествование</td><td>Газеты передают, что 17 декабря в частной квартире состоялось чествование Манасевича-Мануйлова по поводу его освобождения</td><td>1917</td></tr><tr><td>честить</td><td>Когда возражал правый социалист-революционер, какой-то студент и начал честить советскую власть — бурные аплодисменты</td><td>1918</td></tr><tr><td>честолюбец</td><td>Я не знаю, является ли епископ Виктор честолюбцем, добивался ли он своего назначения на этот пост</td><td>1933</td></tr><tr><td>чинный</td><td>Пока духовные лица вели по-гречески чинный разговор, экзотическая матушка угощала нас мастикой и вареньем</td><td>1921</td></tr><tr><td>чрез</td><td>Они не приняли, говоря, что это должно быть сделано официально, то есть чрез меня</td><td>1909</td></tr><tr><td>чудный</td><td>Да и за чтобы было папе, такому чудному человеку, такое тяжёлое наказание от Бога</td><td>1911</td></tr><tr><td>чуждаться</td><td>Помню, как неприятно мне было, когда последнее время она как-то чуждалась нас, словно стыдилась «городских» перед своими</td><td>1928</td></tr><tr><td>чуяться</td><td>Чуется мне нарождение новой русской интеллигенции</td><td>1922</td></tr><tr><td>шалый</td><td>Он кажется шалым и путаным, а на самом деле — очень деловой и озабоченный</td><td>1923</td></tr><tr><td>шикать</td><td>Слухи эти оправдались лишь в том, что после первого и второго действий кто-то старался шикать, но немедленно поднимались аплодисменты, и потому ничего не выходило</td><td>1904</td></tr><tr><td>шифоньерка</td><td>Еще обставлена она богатой мебелью: столы, шкафы, диваны, рояль, ширмочки, шифоньерки и проч</td><td>1920</td></tr><tr><td>шкап</td><td>Утром перенес в архив, в библиотечном здании, из канцелярского шкапа все нужные русские бумаги</td><td>1904</td></tr><tr><td>шпионство</td><td>Этот, снова пересмотрев мои вещи, нашел мною набросанную карту железных дорог той местности и на этом основании заподозрил меня в шпионстве</td><td>1920</td></tr><tr><td>щегольство</td><td>Их фильмы, и особенно последний, — «Новый Вавилон» — отличаются цельностью, законченностью и щегольством отделки</td><td>1929</td></tr><tr><td>экзаменоваться</td><td>Экзаменовалось у меня 8 человек: 4 весьма удовлетворительно и 4 удовлетворительно</td><td>1915</td></tr><tr><td>экскурсировать</td><td>В своем любимом месте, на первой и последней стоянке озера Куку-нора, мы вновь экскурсировали в целях полюбоваться или поймать рыбы в светлых водах быстрой речонки</td><td>1908</td></tr><tr><td>экспансивность</td><td>Третьим визитером с обычной экспансивностью влетел ко мне популярнейший эстрадный певец и директор политического ревю (обозрения) сам «обожаемый» Карл Герхард</td><td>1939</td></tr><tr><td>энергический</td><td>Буланже, и его преувеличенный восторг и движения слабого физически, но энергического человека, и его спина — все это возбуждало во мне какую-то брезгливость</td><td>1902</td></tr><tr><td>эстетизм</td><td>Приятен у Мережковских некий эстетизм и вообще другой тон жизни</td><td>1929</td></tr><tr><td>юдофобство</td><td>Юдофобство растет всюду, и, неумное всегда, принимает формы печально-комические</td><td>1919</td></tr><tr><td>юмористика</td><td>Это написано всерьез, — но воспринимается как юмористика</td><td>1937</td></tr><tr><td>явствовать</td><td>Из письменных и устных докладов главных начальников снабжений явствует, что лишние запасы имелись и имеются почти всегда в достаточном количестве, например хлеб и овес — в распоряжении тылов фронтов и передаются ими в тылы армий и в то же время до войска не доходят или доходят в недостаточном количестве</td><td>1916</td></tr><tr><td>яснеть</td><td>Вдруг впереди появляется бледное пятно, оно начинает все яснеть и постепенно — не верится глазам — превращается в блик дневного света</td><td>1937</td></tr></tbody></table>',3)]))}const l=d(o,[["render",s],["__file","russian_old_words.html.vue"]]),h=JSON.parse('{"path":"/dictionary/russian_old_words.html","title":"Старые русские слова","lang":"ru-RU","frontmatter":{},"headers":[],"git":{"updatedTime":1705765662000,"contributors":[{"name":"blackchestnut","username":"blackchestnut","email":"alexander.kalinichev@gmail.com","commits":2,"url":"https://github.com/blackchestnut"}],"changelog":[{"hash":"ace370af15f371804f2664d5f21575d61c2a658b","time":1705765662000,"email":"alexander.kalinichev@gmail.com","author":"blackchestnut","message":"Add &quot;творило&quot; to russian_old_words.md"},{"hash":"9c71ae775293f67f0857ca461ea9256c155712e5","time":1662218182000,"email":"alexander.kalinichev@gmail.com","author":"blackchestnut","message":"Add russian old words"}]},"filePathRelative":"dictionary/russian_old_words.md"}');export{l as comp,h as data};
